mapper.insert:
INSERT INTO ec_accounting( id, order_id, create_by, create_date ) SELECT REPLACE(CAST(UUID() as char character set utf8), '-', '') ,ord.id ,#{currentUser.id} ,CURRENT_TIMESTAMP FROM ec_order ord LEFT JOIN ec_accounting accounting ON accounting.order_id = ord.id WHERE ord.id IN ( SELECT DISTINCT ord.id FROM ec_order_item orderItem LEFT JOIN ec_product_item productItem ON orderItem.product_item_id = productItem.id LEFT JOIN ec_product product ON productItem.product_id = product.id LEFT JOIN ec_brand brand ON product.brand_id = brand.id LEFT JOIN ec_order ord ON orderItem.order_id = ord.id LEFT JOIN ec_payment payment ON payment.id = ord.payment_id LEFT JOIN ec_gateway_type gateway ON payment.gateway_type_id = gateway.id WHERE ord.payment_status = 1
AND ord.order_status = 2 AND ord.shipping_status = 2
AND brand.supplier_id = #{id}
AND payment.payment_date >= STR_TO_DATE(#{sqlMap.st},"%Y-%m-%d %H:%i:%s")
AND ADDDATE(STR_TO_DATE(#{sqlMap.et},"%Y-%m-%d %H:%i:%s"),INTERVAL 1 DAY) >= payment.payment_date
AND orderItem.order_item_type = #{sqlMap.orderType}
AND gateway.id = #{sqlMap.gateway}
AND (ord.order_number like '%${sqlMap.keyword}%' OR payment.payment_no like '%${sqlMap.keyword}%')
AND ord.reconciliation_status = #{sqlMap.reconciliationStatus}
GROUP BY ord.id ) AND accounting.id IS NULL
