SELECT

FROM ec_answer_sheet a

WHERE 1=1

    AND a.id = #{id}


    AND a.customer_id = #{customer.id}


    AND a.questionnaire_id = #{questionnaire.id}


    AND a.id IN (SELECT answer_sheet_id FROM  ec_activity_answer_sheet WHERE activity_id = #{sqlMap.activityId})

SELECT

FROM ec_answer_sheet a

WHERE a.id = #{id}
