function isNull(a){return undefined===a||null===a||new RegExp("^[ ]?$").test(a)}function getDefault(b,a){if(isNull(b)){return a}return b}function isInteger(b){var a=/^[-]{0,1}[0-9]{1,}$/;return a.test(b)}function isNumber(c){var a="^[0-9]+$";var b=new RegExp(a);if(c.search(b)!=-1){return true}else{return false}}function isDecimal(b){if(isInteger(b)){return true}var a=/^[-]{0,1}(\d+)[\.]+(\d+)$/;if(a.test(b)){return true}else{return false}}function isEmail(b){var a=/^[-_A-Za-z0-9]+@([_A-Za-z0-9]+\.)+[A-Za-z0-9]{2,3}$/;if(a.test(b)){return true}return false}function isMoney(c){if(isNumber(c)){return true}var a="^[0-9]+[.][0-9]{0,3}$";var b=new RegExp(a);if(b.test(c)){return true}else{return false}}function isLetter(c){var a="^[0-9a-zA-Z_]+$";var b=new RegExp(a);if(b.test(c)){return true}else{return false}}function isChinaOrNumbOrLett(c){var a="^[0-9a-zA-Z\u4e00-\u9fa5]+$";var b=new RegExp(a);if(b.test(c)){return true}else{return false}}function checkEmail(a){var b=/^[\w-]+(\.[\w-]+)*@[\w-]+(\.[\w-]+)+$/;if(b.test(a)){return true}else{alert("您输入的Email地址格式不正确！");return false}};