mapper.select:
SELECT
FROM ec_coupon a WHERE 1=1
AND is_activate= 1 AND del_flag= 0 AND NOW() > start_time AND end_time > NOW()
AND id = #{id}
AND type = #{type}

mapper.update:
UPDATE ec_coupon SET
del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}

UPDATE ec_coupon SET
is_activate = #{ACTIVATED}
WHERE id = #{id}
