INSERT INTO `cms_article_data` VALUES ('04ffc3ae0c4e4b528ff19a35af36f654', '<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<span style=\"font-size:14px;font-family:&quot;\">亲爱的V猫店主：</span>\r\n</p>\r\n<p>\r\n	<span style=\"font-size:14px;font-family:&quot;\">我们将于今晚（11月18日）20:00上架【周末团购】三只馋猫冷吃兔120g*2的新规格产品，以下是产品信息预览：</span>\r\n</p>\r\n<p>\r\n	<span style=\"font-size:14px;font-family:&quot;\">产品规格：120g*2</span>\r\n</p>\r\n<p>\r\n	<span style=\"font-size:14px;font-family:&quot;\">团购价格：29.9元</span>\r\n</p>\r\n<p>\r\n	<span style=\"font-size:14px;font-family:&quot;\">白金店主销售利润：9.8元</span>\r\n</p>\r\n<p>\r\n	<span style=\"font-size:14px;font-family:&quot;\">特约店主销售利润：6元</span>\r\n</p>\r\n<p>\r\n	<span style=\"font-size:14px;font-family:&quot;\">活动时间：11月18日20:00~11月20日21:00</span>\r\n</p>\r\n<p>\r\n	<span style=\"font-size:14px;font-family:&quot;\">欢迎各位店主代理销售，感谢您的支持！</span>\r\n</p>', '', '', '1');
