INSERT INTO `cms_article_data` VALUES ('0e1e79c62c5742b0810035e1041806eb', '<p>\r\n	<span style=\"font-family:\'Microsoft YaHei\';font-size:16px;color:#E53333;\"><strong><br />\r\n</strong></span> \r\n</p>\r\n<p>\r\n	<span style=\"font-family:\'Microsoft YaHei\';font-size:16px;color:#E53333;\"><strong>首先</strong></span><span style=\"font-family:\'Microsoft YaHei\';font-size:14px;line-height:1.5;\">，V猫平台是基于社交平台产生的，所以要想推广自己的产品就需要让社交圈的好友们都知道。将产品通过链接分享到社交平台是第一种方法。需要注意的是，空洞的分享内容是不能引起朋友们关注的，我们需要更有趣的内容去包装销售的产品。</span> \r\n</p>\r\n<br />\r\n<span style=\"font-size:14px;font-family:\'Microsoft YaHei\';\"><span style=\"font-size:16px;color:#E53333;\"><strong>其次</strong></span>，V猫店主在有限时间内只能代理一类产品，我们可以利用与产品相关的互联网平台进行推广，比如：贴吧、论坛社区、QQ群等。当我们成为这些社交平台的一员时，不要忙着发广告，经常在线上活跃，分享自己的生活趣事或者热情的给各位网友排疑解难（比如：你代理化妆品，在很多化妆论坛中可以发帖分享一些护肤流程或者是化妆教学视频等），让想要更多资源的网友成为你的精准客户。</span><br />\r\n<br />\r\n<p>\r\n	<span style=\"font-size:14px;font-family:\'Microsoft YaHei\';\"><span style=\"font-size:16px;color:#E53333;\"><strong>当</strong></span>他们相信你是这个领域中的大湿时，产品才有足够的信服度。所以，V猫店主们，你们需要回炉重造，多读书多看报，你需要足够充足的专业知识储备才能回答更多人的疑问。</span> \r\n</p>\r\n<br />\r\n<img src=\"http://201106.image.myqcloud.com/201106/0/531c3c3a-baa9-4b54-b4b2-a76bf0d02cdf/original\" width=\"600\" height=\"300\" alt=\"\" /><br />', '', '', '1');
INSERT INTO `cms_article_data` VALUES ('0f15f4a6c4634ac1ab9ec350ab45e35f', '<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<span style=\"font-size:14px;font-family:&quot;\">亲爱的V猫店主：</span>\r\n</p>\r\n<p>\r\n	<span style=\"font-size:14px;font-family:&quot;\">由于市场价格波动，汉源双溪白凤桃的销售价格有所调整。针对之前预售期已支付的店主订单，我们有三种处理方式：</span>\r\n</p>\r\n<p>\r\n	<span style=\"font-size:14px;font-family:&quot;\">1、退还差价；</span>\r\n</p>\r\n<p>\r\n	<span style=\"font-size:14px;font-family:&quot;\">2、多发一件产品；</span>\r\n</p>\r\n<p>\r\n	<span style=\"font-size:14px;font-family:&quot;\">3、全额退款。</span><span style=\"font-size:14px;font-family:&quot;\"></span>\r\n</p>\r\n<p>\r\n	<span style=\"font-size:14px;font-family:&quot;\">稍后，【售后喵</span><span style=\"font-size:14px;font-family:&quot;\">】将与您取得联系，处理相关问题。</span>\r\n</p>\r\n<p>\r\n	<span style=\"font-size:14px;font-family:&quot;\">感谢您的支持，谢谢！</span>\r\n</p>', '', '', '1');
INSERT INTO `cms_article_data` VALUES ('0f4b5104e8f24179b28ab197b563eefe', '<p class=\"MsoNormal\" align=\"left\" style=\"margin:3.75pt 0cm;line-height:13.5pt;\">\r\n	<span style=\"font-size:14px;font-family:\'Microsoft YaHei\';\">您已拥有</span><span lang=\"EN-US\" style=\"font-size:14px;font-family:\'Microsoft YaHei\';\">V</span><span style=\"font-size:14px;font-family:\'Microsoft YaHei\';\">猫小店提供的经销商身份，可以代理销售平台上所有产品，平台所有产品您的客户下单后您都会享受品牌供应商提供的利润返现，自购该产品您同样享受利润返现。入驻供应商已通过平台审核，</span><span lang=\"EN-US\" style=\"font-size:14px;font-family:\'Microsoft YaHei\';\">V</span><span style=\"font-size:14px;font-family:\'Microsoft YaHei\';\">猫小店承诺所有产品假一赔十。</span><span lang=\"EN-US\"><o:p></o:p></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-align:center;\">\r\n	<b><span style=\"font-size:14px;color:#E53333;\"><span style=\"font-family:Microsoft YaHei;\">—— </span><span style=\"color:#FFFFFF;font-family:\'Microsoft YaHei\';background-color:#E53333;\">V</span></span></b><b><span style=\"font-size:14px;color:#E53333;\"><span style=\"color:#FFFFFF;font-family:\'Microsoft YaHei\';background-color:#E53333;\">猫体验官</span><span style=\"font-family:Microsoft YaHei;\"> ——</span></span><span lang=\"EN-US\"><o:p></o:p></span></b> \r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<span style=\"font-size:14px;font-family:\'Microsoft YaHei\';\">您可报名参与每周一次的体验官活动，申请成功后可免费体验</span><span lang=\"EN-US\" style=\"font-size:14px;font-family:\'Microsoft YaHei\';\">V</span><span style=\"font-size:14px;font-family:\'Microsoft YaHei\';\">猫小店指定正规产品（非小样，需承担</span><span lang=\"EN-US\" style=\"font-size:14px;font-family:\'Microsoft YaHei\';\">6</span><span style=\"font-size:14px;font-family:\'Microsoft YaHei\';\">元邮费），并在收到商品一周内完成体验报告，体验报告汇总后的数据决定了该产品能否在平台上销售。优秀店主享受当季度免申请直接体验产品的权利（平台包邮）。</span> \r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<span style=\"font-size:14px;\"><img src=\"http://201106.image.myqcloud.com/201106/0/3f18f12c-4ca2-46d7-9bd9-3ad8b4cf3a04/original\" width=\"900\" height=\"796\" alt=\"\" /><br />\r\n</span> \r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<br />\r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-align:center;\">\r\n	<b><span style=\"font-size:14px;color:#E53333;font-family:\'Microsoft YaHei\';\">—— </span></b><b><span style=\"font-size:14px;color:#E53333;\"><span style=\"color:#FFFFFF;font-family:\'Microsoft YaHei\';background-color:#E53333;\">猫币专区</span><span style=\"font-family:Microsoft YaHei;\"> ——</span></span><span lang=\"EN-US\"><o:p></o:p></span></b> \r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<span style=\"font-size:14px;font-family:\'Microsoft YaHei\';\">针对部分品牌知名度有限但产品质量又有保证的产品，</span><span lang=\"EN-US\" style=\"font-size:14px;font-family:\'Microsoft YaHei\';\">V</span><span style=\"font-size:14px;font-family:\'Microsoft YaHei\';\">猫小店用猫币补贴店主拿样体验。想要获得更多猫币，可通过分享邀请码，邀请好友一起开店或积极参与</span><span lang=\"EN-US\" style=\"font-size:14px;font-family:\'Microsoft YaHei\';\">V</span><span style=\"font-size:14px;font-family:\'Microsoft YaHei\';\">猫平台互动活动的方式获得。</span><span lang=\"EN-US\"><o:p></o:p></span> \r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<span style=\"font-size:14px;\"><img src=\"http://201106.image.myqcloud.com/201106/0/0c38641f-993b-4ad0-9053-ee3a0fbfdd97/original\" width=\"900\" height=\"796\" alt=\"\" /><br />\r\n</span> \r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<span style=\"font-size:14px;\"><br />\r\n</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-align:center;\">\r\n	<b><span style=\"font-size:14px;color:#E53333;\"><span style=\"font-family:Microsoft YaHei;\">—— </span><span style=\"color:#FFFFFF;font-family:\'Microsoft YaHei\';background-color:#E53333;\">分享奖励</span><span style=\"font-family:Microsoft YaHei;\"> ——</span></span><span lang=\"EN-US\"><o:p></o:p></span></b> \r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<span style=\"font-size:14px;font-family:\'Microsoft YaHei\';\">分享奖励是供应商为了鼓励您将产品信息分享到社交圈而提供的现金奖励。</span><span lang=\"EN-US\" style=\"font-size:14px;font-family:\'Microsoft YaHei\';\">V</span><span style=\"font-size:14px;font-family:\'Microsoft YaHei\';\">猫小店将不定期的举行分享活动，次数有限，奖励立即到账，请各位店主注意手速哟。</span> \r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<span style=\"font-size:14px;\"><img src=\"http://201106.image.myqcloud.com/201106/0/7f481d20-62b9-403f-9dae-e967bc031d00/original\" width=\"900\" height=\"796\" alt=\"\" /><br />\r\n</span> \r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<span style=\"font-size:14px;\"><br />\r\n</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-align:center;\">\r\n	<b><span style=\"font-size:14px;color:#E53333;\"><span style=\"font-family:Microsoft YaHei;\">—— </span><span style=\"color:#FFFFFF;font-family:\'Microsoft YaHei\';background-color:#E53333;\">组建社群</span><span style=\"font-family:Microsoft YaHei;\"> ——</span></span><span lang=\"EN-US\"><o:p></o:p></span></b> \r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<span style=\"font-size:14px;font-family:\'Microsoft YaHei\';\">您可以通过分享自己的邀请码，让好友一起加入</span><span lang=\"EN-US\" style=\"font-size:14px;font-family:\'Microsoft YaHei\';\">V</span><span style=\"font-size:14px;font-family:\'Microsoft YaHei\';\">猫小店，并成为您的社群成员。您可以带领您的闺蜜赚钱，也可以教会朋友利用</span><span lang=\"EN-US\" style=\"font-size:14px;font-family:\'Microsoft YaHei\';\">V</span><span style=\"font-size:14px;font-family:\'Microsoft YaHei\';\">猫小店省钱。未来成交的每笔交易，她</span><span lang=\"EN-US\" style=\"font-size:14px;font-family:\'Microsoft YaHei\';\">/</span><span style=\"font-size:14px;font-family:\'Microsoft YaHei\';\">他都有利润返现，您都能获得平台额外提供的</span><span lang=\"EN-US\" style=\"font-size:14px;font-family:\'Microsoft YaHei\';\">10%--20%</span><span style=\"font-size:14px;font-family:\'Microsoft YaHei\';\">分红奖励。</span><span lang=\"EN-US\" style=\"font-size:14px;font-family:\'Microsoft YaHei\';\">V</span><span style=\"font-size:14px;font-family:\'Microsoft YaHei\';\">猫小店的分红有二级，教会您的朋友组建自己的社群，您将获得更客观的收益。</span><span lang=\"EN-US\"><o:p></o:p></span> \r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<span style=\"font-size:14px;\"><img src=\"http://201106.image.myqcloud.com/201106/0/4ebb68ee-afb4-41dc-b52a-ebd2b700e95b/original\" width=\"900\" height=\"796\" alt=\"\" /><br />\r\n</span> \r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<br />\r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-align:center;\">\r\n	<b><span style=\"color:#E53333;font-size:14px;\"><span style=\"font-family:Microsoft YaHei;\">—— </span><span style=\"color:#FFFFFF;font-family:\'Microsoft YaHei\';background-color:#E53333;\">V</span></span></b><b><span style=\"color:#E53333;font-size:14px;\"><span style=\"color:#FFFFFF;font-family:\'Microsoft YaHei\';background-color:#E53333;\">猫会</span><span style=\"font-family:Microsoft YaHei;\"> ——</span></span><span lang=\"EN-US\"><o:p></o:p></span></b> \r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<b><span style=\"font-size:14px;color:#E53333;font-family:\'Microsoft YaHei\';\">好货先锋体验团</span></b><span style=\"font-size:14px;font-family:\'Microsoft YaHei\';\">：组织优秀</span><span lang=\"EN-US\" style=\"font-size:14px;font-family:\'Microsoft YaHei\';\">V</span><span style=\"font-size:14px;font-family:\'Microsoft YaHei\';\">猫店主，一起寻找更多优质好货；</span><b><o:p></o:p></b> \r\n</p>\r\n<p class=\"MsoNormal\" align=\"left\" style=\"line-height:16.9pt;\">\r\n	<b><span style=\"font-size:14px;color:#E53333;font-family:\'Microsoft YaHei\';\">V</span></b><b><span style=\"font-size:14px;color:#E53333;font-family:\'Microsoft YaHei\';\">猫大会</span></b><b><span style=\"font-size:14px;font-family:\'Microsoft YaHei\';\">：</span></b><span style=\"font-size:14px;font-family:\'Microsoft YaHei\';\">定期组织各种线下</span><span lang=\"EN-US\" style=\"font-size:14px;font-family:\'Microsoft YaHei\';\">V</span><span style=\"font-size:14px;font-family:\'Microsoft YaHei\';\">猫店主聚会，邀请互联网行业大咖及优秀</span><span lang=\"EN-US\" style=\"font-size:14px;font-family:\'Microsoft YaHei\';\">V</span><span style=\"font-size:14px;font-family:\'Microsoft YaHei\';\">猫店主分享开店赚钱心得及经验干货；</span><o:p></o:p>\r\n</p>\r\n<p class=\"MsoNormal\" align=\"left\" style=\"line-height:16.9pt;\">\r\n	<b><span style=\"font-size:14px;color:#E53333;font-family:\'Microsoft YaHei\';\">供应商参观考察</span></b><b><span style=\"font-size:14px;font-family:\'Microsoft YaHei\';\">：</span></b><span style=\"font-size:14px;font-family:\'Microsoft YaHei\';\">定期组织</span><span lang=\"EN-US\" style=\"font-size:14px;font-family:\'Microsoft YaHei\';\">V</span><span style=\"font-size:14px;font-family:\'Microsoft YaHei\';\">猫店主赴优秀供应商工厂、基地进行实地学习考察；</span><o:p></o:p>\r\n</p>\r\n<p class=\"MsoNormal\" align=\"left\" style=\"line-height:16.9pt;\">\r\n	<b><span style=\"font-size:14px;color:#E53333;font-family:\'Microsoft YaHei\';\">主题活动</span></b><b><span style=\"font-size:14px;font-family:\'Microsoft YaHei\';\">：</span></b><span style=\"font-size:14px;font-family:\'Microsoft YaHei\';\">定期发起各种主题活动，同时鼓励所有</span><span lang=\"EN-US\" style=\"font-size:14px;font-family:\'Microsoft YaHei\';\">v</span><span style=\"font-size:14px;font-family:\'Microsoft YaHei\';\">猫店主自主发起相关主题活动，</span><span lang=\"EN-US\" style=\"font-size:14px;font-family:\'Microsoft YaHei\';\">V</span><span style=\"font-size:14px;font-family:\'Microsoft YaHei\';\">猫会鼎力支持。</span><o:p></o:p>\r\n</p>', '', '', '1');
