INSERT INTO `cms_article_data` VALUES ('1bf5689cd7b747fdbd74d690b27ec6b8', '<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<span style=\"font-size:14px;font-family:&quot;\">亲爱的V猫店主：</span>\r\n</p>\r\n<p>\r\n	<span style=\"font-size:14px;font-family:&quot;\">明日就正式迎来7天国庆假期了，在工作日的最后一天，V猫有福利。</span>\r\n</p>\r\n<p>\r\n	<span style=\"font-size:14px;font-family:&quot;\">攀枝花凯特芒（8斤装）仅售45.5元（原价68.8元</span><span style=\"font-size:14px;font-family:&quot;\">）</span>\r\n</p>\r\n<p>\r\n	<span style=\"font-size:14px;font-family:&quot;\">限量50份，售完恢复原价！</span>\r\n</p>\r\n<p>\r\n	<span style=\"font-size:14px;font-family:&quot;\">快来抢购吧！！！</span>\r\n</p>', '', '', '1');
INSERT INTO `cms_article_data` VALUES ('1c4d18df93e143428bc491935eafe4fb', '<p class=\"MsoNormal\" style=\"margin-left:0cm;\">\r\n	<b><span style=\"font-family:\'Microsoft YaHei\';font-size:14px;line-height:1.5;color:#E53333;\"></span></b> \r\n</p>\r\n<p class=\"MsoListParagraph\" style=\"margin-left:18.0pt;text-indent:-18.0pt;\">\r\n	<b style=\"text-indent:-18pt;line-height:1.5;\"><b><span style=\"font-size:10.5pt;font-family:\" color:#e53333;\"=\"\"><span style=\"font-family:\'Microsoft YaHei\';color:#E53333;\">1、</span><span style=\"font-weight:normal;font-size:7pt;line-height:normal;font-family:\'Times New Roman\';\"> </span></span></b><b><span style=\"font-size:10.5pt;font-family:\" color:#e53333;\"=\"\"><span style=\"font-family:\'Microsoft YaHei\';color:#E53333;\">什么是</span><span style=\"font-family:\'Microsoft YaHei\';color:#E53333;\">V</span><span style=\"font-family:\'Microsoft YaHei\';color:#E53333;\">猫小店？</span></span></b></b> \r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<b><span style=\"font-size:14px;font-family:\'Microsoft YaHei\';color:#333333;\">V</span><span style=\"font-size:14px;font-family:\'Microsoft YaHei\';color:#333333;\">猫小店是一个</span><span style=\"font-size:10.5pt;font-family:\'Microsoft YaHei\';color:#333333;\"><span style=\"font-size:14px;\">以特色农副产</span><span style=\"font-size:14px;\">品及美食为主的社交电商平台</span></span><span style=\"font-size:14px;font-family:\'Microsoft YaHei\';color:#333333;\">，同时也是一个</span><span style=\"font-size:14px;font-family:\'Microsoft YaHei\';color:#333333;\">简单的手机开店赚钱工具</span><span style=\"font-size: 14px; color: rgb(51, 51, 51);\" color:#3e3e3e;\"=\"\"><span style=\"color:#333333;font-family:\'Microsoft YaHei\';font-size:14px;\">。</span></span></b><b style=\"line-height:1.5;\"><span style=\"font-size: 14px;\" color:#3e3e3e;\"=\"\"><span style=\"color:#333333;font-family:\'Microsoft YaHei\';font-size:14px;\">通过互联网技术，为个人提供免费的手机开店技术支持、优质的货源支持、可靠的支付和物流支持、系统的开店培训支持。</span></span></b> \r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<b><span style=\"font-size:10.5pt;font-family:\" color:#3e3e3e;\"=\"\"><span style=\"color:#333333;font-family:\'Microsoft YaHei\';\"><br />\r\n</span></span></b> \r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<b><b><span style=\"font-size:10.5pt;font-family:\'Microsoft YaHei\';color:#E53333;\">2</span></b><b><span style=\"font-size:10.5pt;font-family:\" color:#e53333;\"=\"\"><span style=\"font-family:\'Microsoft YaHei\';color:#E53333;\">、</span><span style=\"font-family:\'Microsoft YaHei\';color:#E53333;\">V</span><span style=\"font-family:\'Microsoft YaHei\';color:#E53333;\">猫店主的收入模式？</span></span></b><span style=\"font-size:11.5pt;font-family:\" color:#3e3e3e;\"=\"\"></span></b> \r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<b><span style=\"font-size:10.5pt;font-family:\" color:#3e3e3e;\"=\"\"><span style=\"color:#333333;font-family:\'Microsoft YaHei\';\">根据</span><span style=\"color:#333333;font-family:\'Microsoft YaHei\';\">V</span><span style=\"color:#333333;font-family:\'Microsoft YaHei\';\">猫小店钻石店主和白金店主身份设置，赚钱方式也分为两种：</span><span></span></span></b> \r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<b><b><span style=\"font-size:18pt;font-family:\'Microsoft YaHei\';color:#E53333;\"> 特约店主&nbsp;</span></b><span style=\"font-size:10.5pt;font-family:;\" \"=\"\"><span style=\"font-family:\'Microsoft YaHei\';color:#333333;\">以零售为</span><span style=\"font-family:\'Microsoft YaHei\';color:#333333;\">主，主要以卖货给好友获得</span><span style=\"font-family:\'Microsoft YaHei\';color:#333333;\">销售佣金。</span><span></span></span></b> \r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<b><b><span style=\"font-size:18pt;font-family:\'Microsoft YaHei\';color:#E53333;\">白金店主<span style=\"color:#333333;\">&nbsp;</span></span></b><span style=\"font-size:10.5pt;font-family:\'Microsoft YaHei\';color:#333333;\">赚钱方式为：</span><span style=\"font-size:10.5pt;font-family:\" color:red;\"=\"\"><span style=\"font-family:\'Microsoft YaHei\';color:#333333;\">销售佣金</span><span style=\"font-family:\'Microsoft YaHei\';color:#333333;\">+销售分红+</span><span style=\"font-family:\'Microsoft YaHei\';color:#333333;\">团队分红</span><span style=\"font-family:\'Microsoft YaHei\';color:#333333;\">+</span><span style=\"font-family:\'Microsoft YaHei\';color:#333333;\">伯乐奖励</span><span></span></span></b> \r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<b><span style=\"font-size:14px;font-family:\'Microsoft YaHei\';color:#E53333;\">销售佣金</span></b> \r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<b><span style=\"font-size:14px;font-family:\'Microsoft YaHei\';color:#333333;\">卖货给好友</span><span style=\"font-size:14px;font-family:\'Microsoft YaHei\';color:#333333;\">获得</span><span style=\"font-size:14px;font-family:\'Microsoft YaHei\';color:#333333;\">利润最大化</span><span style=\"font-size:10.5pt;font-family:\" color:#3e3e3e;\"=\"\"><span style=\"font-family:\'Microsoft YaHei\';color:#333333;font-size:14px;\">的销售佣金；</span><span></span></span></b> \r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<b><span style=\"font-size:10.5pt;font-family:\" color:#3e3e3e;\"=\"\"><span style=\"font-family:Microsoft YaHei;\"><br />\r\n</span></span></b> \r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<b><span style=\"font-size:10.5pt;font-family:\" color:#3e3e3e;\"=\"\"><span style=\"font-family:Microsoft YaHei;\"></span></span></b> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"line-height:11.0pt;\">\r\n	<b><b><span style=\"color:#E53333;font-size:14px;\">销售分红</span></b></b> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"line-height:11pt;display:inline !important;\">\r\n	<b><span style=\"color:#333333;font-size:14px;\">身为白金店主的您不仅能依靠卖货赚得销售佣金，与您绑定的团队成员（特约店主）每完成一笔交易，您还将获得其销售额8%--20%的销售分红，团队成员越多赚得越多；</span></b> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"line-height:11.0pt;\">\r\n	<b><o:p></o:p></b> \r\n</p>\r\n<b><br />\r\n</b> \r\n<p>\r\n	<br />\r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<b><span style=\"font-size:10.5pt;font-family:\" color:red;\"=\"\"><span style=\"font-family:Microsoft YaHei;\"></span></span></b> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"line-height:11.0pt;\">\r\n	<b><b><span style=\"color:#E53333;font-size:14px;\">团队分红：</span><span lang=\"EN-US\"><o:p></o:p></span></b></b> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"line-height:11.0pt;\">\r\n	<b><span style=\"font-size:14px;color:#333333;line-height:1.5;\">若您的团队有成员升级为白金店主了，虽您将不再获得该成员个人零售所产生的销售分红；但别担心，因团队成员将实行终身绑定制度，你将能够获得该成员自行发展团队后，以他为首整个团队销售总额2%的分红奖励，团队分红按周结算。</span><o:p></o:p></b> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"line-height:11.0pt;\">\r\n	<b><br />\r\n</b> \r\n</p>\r\n<b><span style=\"font-size:10.5pt;font-family:\" color:#3e3e3e;\"=\"\"><span style=\"font-family:Microsoft YaHei;\"></span><span style=\"font-family:\'Microsoft YaHei\';\"></span><span style=\"font-family:Microsoft YaHei;\"></span><span style=\"font-family:\'Microsoft YaHei\';\"></span><span style=\"font-family:Microsoft YaHei;\"></span><span style=\"font-family:\'Microsoft YaHei\';\"></span><span style=\"font-family:Microsoft YaHei;\"></span><span></span></span></b> \r\n<p>\r\n	<br />\r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<b><span style=\"font-size:10.5pt;font-family:\'Microsoft YaHei\';\"></span></b> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"line-height:11.0pt;\">\r\n	<b><b><span style=\"color:#E53333;font-size:14px;\">伯乐奖励：</span><span lang=\"EN-US\"><o:p></o:p></span></b></b> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"line-height:11.0pt;\">\r\n	<b><span style=\"color:#333333;font-size:14px;\">带领团队成员共同成长，当有团队成员（特约店主）升级为白金店主后，您还将获得一次性100元现金奖励。</span><o:p></o:p></b> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"line-height:11.0pt;\">\r\n	<b><br />\r\n</b> \r\n</p>\r\n<b><span style=\"font-size:10.5pt;font-family:;\" \"=\"\"><span style=\"font-family:Microsoft YaHei;\"></span><span style=\"font-family:\'Microsoft YaHei\';\"></span><span style=\"font-family:Microsoft YaHei;\"></span><span></span></span></b> \r\n<p>\r\n	<br />\r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<b><b><span style=\"font-size:14px;font-family:\'Microsoft YaHei\';color:#E53333;\">3</span></b><b><span style=\"font-size:10.5pt;font-family:\" co