INSERT INTO `cms_article_data` VALUES ('24c2c1ae92ef4482b3c3c5550d9e0e14', '<p class=\"MsoNormal\">\r\n	<span style=\"font-family:微软雅黑;font-size:10.5000pt;\"><br />\r\n</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-align:left;\">\r\n	<span style=\"font-family:微软雅黑;font-size:10.5000pt;\">各位V猫店主们，独自认识新鲜事的过程是不是困难重重；</span><span style=\"font-family:微软雅黑;font-size:10.5pt;line-height:1.5;\">想要学习如何省钱又赚钱却不得要领！</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-align:left;\">\r\n	<br />\r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-align:left;\">\r\n	<span style=\"font-family:微软雅黑;font-size:10.5pt;line-height:1.5;\">人说，站在前人栽的树下才好乘凉，现在V猫平台给还在迷惑的V猫店主们开个小灶，</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-align:left;\">\r\n	<span style=\"font-family:微软雅黑;font-size:10.5000pt;\">给个机会向V猫大湿提问咨询，快扫二维码加入V猫小店分享群，一起来抢名额抢先机！</span><span style=\"font-family:微软雅黑;font-size:10.5000pt;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-align:left;\">\r\n	<span style=\"font-family:微软雅黑;font-size:10.5000pt;\">注意注意！！</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-align:left;\">\r\n	<span style=\"font-family:微软雅黑;font-size:10.5000pt;\"><span style=\"color:#E53333;\">前方高能！</span><span style=\"font-family:微软雅黑;font-size:14.44444465637207px;line-height:23.33333396911621px;white-space:normal;\"><span style=\"font-size:18px;color:#E53333;\">前方高能！</span><span style=\"font-family:微软雅黑;font-size:18px;line-height:23.33333396911621px;white-space:normal;color:#E53333;\">前方高能！</span></span></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-align:left;\">\r\n	<span style=\"font-family:微软雅黑;font-size:10.5000pt;\"><span style=\"font-family:微软雅黑;font-size:14.44444465637207px;line-height:23.33333396911621px;white-space:normal;\"><span style=\"font-family:微软雅黑;font-size:14.44444465637207px;line-height:23.33333396911621px;white-space:normal;\"><span style=\"color:#E53333;\">前方高能！</span><span style=\"font-family:微软雅黑;font-size:14.44444465637207px;line-height:23.33333396911621px;white-space:normal;\"><span style=\"font-size:18px;color:#E53333;\">前方高能！</span><span style=\"font-family:微软雅黑;font-size:18px;line-height:23.33333396911621px;white-space:normal;color:#E53333;\">前方高能！</span></span></span></span></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-align:left;\">\r\n	<span style=\"font-family:微软雅黑;font-size:10.5000pt;\"><span style=\"font-family:微软雅黑;font-size:14.44444465637207px;line-height:23.33333396911621px;white-space:normal;\"><span style=\"font-family:微软雅黑;font-size:14.44444465637207px;line-height:23.33333396911621px;white-space:normal;\"><span style=\"font-family:微软雅黑;font-size:14.44444465637207px;line-height:23.33333396911621px;white-space:normal;\"><span style=\"font-family:微软雅黑;font-size:18px;line-height:23.33333396911621px;white-space:normal;color:#E53333;\"><br />\r\n</span></span></span></span></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-align:left;\">\r\n	<span style=\"font-family:微软雅黑;\"><span style=\"font-size:14.44444465637207px;line-height:23.33333396911621px;\">V猫小店互动群有<span style=\"font-size:24px;color:#E53333;\"><strong>福利</strong></span></span></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-align:left;\">\r\n	<span style=\"font-family:微软雅黑;\"><span style=\"font-size:14.44444465637207px;line-height:23.33333396911621px;\"><span style=\"font-size:24px;color:#E53333;\"><strong>微信，QQ群抢红包&nbsp;</strong></span></span></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-align:left;\">\r\n	<span style=\"font-family:微软雅黑;\"><span style=\"font-size:14.44444465637207px;line-height:23.33333396911621px;\"><span style=\"font-size:24px;color:#E53333;\"><span style=\"font-family:微软雅黑;font-size:14.44444465637207px;line-height:23.33333396911621px;white-space:normal;color:#000000;\">不定期的发红包回馈V猫店主</span><span style=\"color:#000000;\"></span></span></span></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-align:left;\">\r\n	<span style=\"font-family:微软雅黑;\"><span style=\"font-size:14.44444465637207px;line-height:23.33333396911621px;\"><span style=\"font-size:24px;color:#E53333;\"><strong>V猫体验官名额限量发放</strong></span></span></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-align:left;\">\r\n	<span style=\"font-family:微软雅黑;\"><span style=\"font-size:14.44444465637207px;line-height:23.33333396911621px;\"><span style=\"font-size:24px;color:#E53333;\"><strong>V猫大湿在线分享“生意经”</strong></span></span></span> \r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<img src=\"http://201106.image.myqcloud.com/201106/0/d57735d8-9ffc-47c7-ab3b-c041cd2f0b55/original\" width=\"614\" height=\"616\" alt=\"\" /><a href=\"http://jq.qq.com/?_wv=1027&k=WyOdec\" target=\"_blank\"><img src=\"http://201106.image.myqcloud.com/201106/0/d78ea503-9693-47c7-ad9f-2cacf02ee500/original\" width=\"614\" height=\"616\" alt=\"\" /></a> \r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<img src=\"http://201106.image.myqcloud.com/201106/0/df7c18b5-11ff-4891-93a4-09241b7d9356/original\" width=\"600\" height=\"300\" alt=\"\" /> \r\n</p>', '', '', '1');
INSERT INTO `cms_article_data` VALUES ('257ebfe1adc24ae288bfc31d3b3c21e5', '<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<span style=\"font-family:&quot;font-size:14px;\">亲爱的V猫店主：</span>\r\n</p>\r\n<p>\r\n	<span style=\"font-family:&quot;font-size:14px;\">由于库存紧张，<span style=\"color:#E53333;\"><strong>【阳光玫瑰葡萄】</strong></span>将于<span style=\"color:#E53333;\"><strong>8月11日</strong></span>（星期四）9：30下架，请各位店主抓紧时间下单，第二批次订单将统一于8月11日<span style=\"font-family:&quot;font-size:14px;line-height:21px;white-space:normal;\">（星期四）</span>17:00发货。</span>\r\n</p>\r\n<p>\r\n	<span style=\"font-family:&quot;font-size:14px;\">感谢您的支持，谢谢！</span>\r\n</p>', '', '', '1');
INSERT INTO `cms_article_data` VALUES ('25bbc7d3ff6a4196aa616c842608d8a7', '<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	亲爱的V猫店主们，若收到客户的坏果反馈，正确的售后赔付流程如下：\r\n</p>\r\n<p>\r\n	1、请提醒客户，若出现坏果烂果等问题，请尽量不要拒签；\r\n</p>\r\n<p>\r\n	2、将快递开箱，拍照，照片中同时出现坏果情况和物流包装；\r\n</p>\r\n<p>\r\n	3、联系【售后喵】处理赔付问题，记得提供快递单号和相关照片。\r\n</p>\r\n以上，请知悉，感谢您们的支持！<br />', '', '', '1');
INSERT INTO `cms_article_data` VALUES ('2685ce67c16b499f80f77fdc3740373e', '<p class=\"MsoNormal\" style=\"margin-left:0cm;\">\r\n	<b><br />\r\n</b> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"margin-left:0cm;\">\r\n	<b>首先</b><b></b><span></span><span style=\"color:#000000;\">，</span><span style=\"font-size:14px;line-height:1.5;\">V</span><span style=\"font-size:14px;line-height:1.5;\">猫平台是基于社交平台产生的，所以要想推广自己的产品就需要让社交圈的好友们都知道。</span><span style=\"color:#E53333;font-size:14px;line-height:1.5;\">复制产品链接或产品实物</span><span style=\"color:#E53333;font-size:14px;line-height:1.5;\">信息</span><span style=\"color:#E53333;font-size:14px;line-height:1.5;\">分享到社交平台</span><span style=\"font-size:14px;line-height:1.5;\">（微信朋友圈、微博、</span><span style=\"font-size:14px;line-height:1.5;\">QQ</span><span style=\"font-size:14px;line-height:1.5;\">空间、美拍）是第一种方法。需要注意的是，空洞的分享内容是不能引起朋友们关注的，因此需要寻找</span><span style=\"color:#E53333;\"><span style=\"color:#000000;font-size:14px;line-height:1.5;\">和</span><span style=\"font-size:14px;line-height:1.5;\">代理产品相关的内容</span></span><span style=\"font-size:14px;line-height:1.5;\">进行</span><span style=\"color:#E53333;font-size:14px;line-height:1.5;\">包装销售</span><span style=\"font-size:14px;line-height:1.5;\">。</span><span></span> \r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<br />\r\n<b><span style=\"font-size:14px;line-height:1.5;\">其次</span></b><span style=\"font-size:14px;line-height:1.5;\">，</span><span style=\"color:#E53333;font-size:14px;line-height:1.5;\">固定代理产品</span><span style=\"font-size:14px;line-height:1.5;\">，可以</span><span style=\"color:#000000;font-size:14px;line-height:1.5;\">利用与产品相关的互联网平台进行推广</span><span style=\"font-size:14px;line-height:1.5;\">，比如：</span><span style=\"color:#E53333;font-size:14px;line-height:1.5;\">贴吧、论坛社区、</span><span style=\"color:#E53333;font-size:14px;line-height:1.5;\">QQ</span><span style=\"color:#E53333;font-size:14px;line-height:1.5;\">群</span><span style=\"font-size:14px;line-height:1.5;\">等。当我们成为这些社交平台的一员时，不要忙着发广告，经常在线上活跃，分享自己的生活趣事或者热情的给各位网友排疑解难（比如：你代理化妆品，在很多化妆论坛中可以发帖分享一些护肤流程或者是化妆教学视频等），以此来锁定</span><span style=\"font-size:14px;line-height:1.5;\">精准客户。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"margin-left:0cm;\">\r\n	<b><span style=\"font-size:14px;line-height:1.5;\">当</span></b><span style=\"font-size:14px;line-height:1.5;\">他们相信你是这个领域中的大湿时，产品才有足够的信服度。所以，</span><span style=\"font-size:14px;line-height:1.5;\">V</span><span style=\"font-size:14px;line-height:1.5;\">猫店主们，你们需要回炉重造，多读书多看报，你需要足够充足的专业知识储备才能回答更多人的疑问。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"margin-left:0cm;\">\r\n	<span style=\"font-size:14px;line-height:1.5;\"><br />\r\n</span> \r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<br />\r\n</p>\r\n<p class=\"MsoNormal\" style=\"white-space:normal;line-height:26.666667938232422px;\">\r\n	<b><b><b>详询：分别加入联系各庄园管家</b></b></b>\r\n</p>\r\n<p class=\"MsoNormal\" style=\"white-space:normal;line-height:26.666667938232422px;\">\r\n	<b><b><b><img src=\"http://image.v-cat.cn/201106/0/1c8eabc4-f2e9-4d44-9503-01f9fb9a6444/original\" width=\"637\" height=\"1348\" alt=\"\" /><br />\r\n</b></b></b>\r\n</p>\r\n<p>\r\n	<br />\r\n</p>', '', '', '1');
INSERT INTO `cms_article_data` VALUES ('26e8021dd5564164878609c957ce07dc', '<p>\r\n	<br />\r\n</p>\r\n<p class=\"MsoNormal\" align=\"center\" style=\"margin-left:0cm;text-align:center;\">\r\n	<b><span style=\"font-size:14px;font-family:\'Microsoft YaHei\';color:white;background:#E53333;\">【成都宝琪品牌】</span></b><span style=\"font-size:9pt;\"></span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:10.5pt;font-family:\" color:#222222;\"=\"\"><span style=\"font-family:\'Microsoft YaHei\';font-size:14px;\">成都宝琪食品有限公司是一家专门生产台湾特产的食品公司，生产糕饼和牛轧糖的企业。公司一贯秉承“品质为上，诚信第一”的经营宗旨，拥有土凤梨酥，鲜花饼、手工饼干、牛轧糖、蛋黄酥的生产线，</span><span style=\"font-family:\'Microsoft YaHei\';font-size:14px;\">产品覆盖全国各地，并在行业内享有美誉。</span><span></span></span> \r\n</p>\r\n<img src=\"http://201106.image.myqcloud.com/201106/0/ffbb8b7d-fea0-4942-a267-7c53bf75fba3/original\" width=\"749\" height=\"526\" alt=\"\" /> \r\n<p>\r\n	<br />\r\n</p>\r\n<p class=\"MsoNormal\" align=\"center\" style=\"margin-left:0cm;text-align:center;\">\r\n	<b><span style=\"font-size:14px;font-family:\'Microsoft YaHei\';color:#666666;\">为什么代理【宝岛金砖】玫瑰鲜花饼</span></b><b><span style=\"font-size:14px;font-family:\'Microsoft YaHei\';color:#666666;\">？？？</span></b><span style=\"font-size:9pt;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\" align=\"center\" style=\"margin-left:0cm;text-align:center;\">\r\n	<b><span style=\"font-size:16px;font-family:\'Microsoft YaHei\';color:white;background:#E53333;\">【丰厚的代理收入】</span></b><span style=\"font-size:9pt;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"margin-left:0cm;\">\r\n	<span style=\"font-size:14px;font-family:\'Microsoft YaHei\';\">&nbsp;</span> \r\n</p>\r\n<p class=\"MsoListParagraph\" style=\"margin-left:18.0pt;text-indent:-18.0pt;\">\r\n	<span style=\"font-size:10.5pt;font-family:宋体;\"><span style=\"font-size:14px;font-family:\'Microsoft YaHei\';\">1、</span><span style=\"font-size:14px;line-height:normal;font-family:\'Microsoft YaHei\';\">&nbsp;</span></span><span style=\"font-size:14px;font-family:\'Microsoft YaHei\';color:#E53333;\">单品代理利润高达</span><span style=\"font-size:14px;color:#E53333;font-family:\'Microsoft YaHei\';\">31%</span><span style=\"font-size:14px;font-family:\'Microsoft YaHei\';\">（例：代理售价为</span><span style=\"font-size:14px;font-family:\'Microsoft YaHei\';\">60</span><span style=\"font-size:14px;font-family:\'Microsoft YaHei\';\">元，利润高达</span><span style=\"font-size:14px;font-family:\'Microsoft YaHei\';\">19</span><span style=\"font-size:14px;font-family:\'Microsoft YaHei\';\">元）。</span> \r\n</p>\r\n<p class=\"MsoListParagraph\" style=\"margin-left:18.0pt;text-indent:-18.0pt;\">\r\n	<span style=\"font-size:10.5pt;font-family:宋体;\"><span style=\"font-size:14px;font-family:\'Microsoft YaHei\';\">2、</span><span style=\"font-size:14px;line-height:normal;font-family:\'Microsoft YaHei\';\">&nbsp;</span></span><span style=\"font-size:14px;font-family:\'Microsoft YaHei\';color:#E53333;\">战队分红高达代理利润的</span><span style=\"font-size:14px;color:#E53333;font-family:\'Microsoft YaHei\';\">10%—20%</span><span style=\"font-size:14px;font-family:\'Microsoft YaHei\';\">（例：战队好友销售出价值</span><span style=\"font-size:14px;font-family:\'Microsoft YaHei\';\">60</span><span style=\"font-size:14px;font-family:\'Microsoft YaHei\';\">元的鲜花饼，好友赚的</span><span style=\"font-size:14px;font-family:\'Microsoft YaHei\';\">60</span><span style=\"font-size:14px;font-family:\'Microsoft YaHei\';\">元的代理利润的同时你还将获得额外战队分红）。</span><span style=\"font-size:9pt;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"margin-left:0cm;\">\r\n	<span style=\"font-size:14px;font-family:\'Microsoft YaHei\';\">3、</span><span style=\"font-size:14px;font-family:\'Microsoft YaHei\';\">产品不定期参加</span><span style=\"font-size:14px;font-family:\'Microsoft YaHei\';color:#E53333;\">分享奖励活动</span><span style=\"font-size:14px;font-family:\'Microsoft YaHei\';\">，将产品信息分享到社交圈，获得现金奖励（活动详情请关注推送消息）。</span><span style=\"font-size:9pt;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"margin-left:0cm;\">\r\n	<span style=\"font-size:14px;font-family:\'Microsoft YaHei\';\">&nbsp;</span> \r\n</p>\r\n<p class=\"MsoNormal\" align=\"center\" style=\"margin-left:0cm;text-align:center;\">\r\n	<b><span style=\"font-size:16px;font-family:\'Microsoft YaHei\';color:white;background:#E53333;\">【强大的产品优势】</span></b><span style=\"font-size:9pt;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"margin-left:0cm;\">\r\n	<span style=\"font-size:14px;font-family:\'Microsoft YaHei\';\">&nbsp;</span> \r\n</p>\r\n<p class=\"MsoListParagraph\" style=\"margin-left:18.0pt;text-indent:-18.0pt;\">\r\n	<span style=\"font-family:\'Microsoft YaHei\';font-size:14px;\">1、</span><b><span style=\"font-size:14px;font-family:\'Microsoft YaHei\';color:#E53333;\">台湾原作坊</span></b><b><span style=\"font-size:14px;color:#E53333;font-family:\'Microsoft YaHei\';\">&nbsp; </span></b><b><span style=\"font-size:14px;font-family:\'Microsoft YaHei\';color:#E53333;\">纯手工制作：</span></b><span style=\"font-size:10.5pt;font-family:;\" \"=\"\"><span style=\"font-family:\'Microsoft YaHei\';font-size:14px;\">宝岛金砖品牌为保证还原台湾风味鲜花饼的口感，在制作方面专门采用了原作坊配方。以纯手工的方式制作，从鲜花采摘、储存、拣花再到制饼，复杂工序中通过对细节的层层把控，制作出道道美味。和市面上鲜花饼的饼状不同，宝岛金砖根据品牌定位将鲜花饼做成了金黄色的长方形，造型上看上去就像是一块金砖。</span><span></span></span> \r\n</p>\r\n<p class=\"MsoListParagraph\" style=\"margin-left:18.0pt;text-indent:-18.0pt;\">\r\n	<span style=\"font-size:10.5pt;font-family:;\" \"=\"\"><span style=\"font-family:\'Microsoft YaHei\';font-size:14px;\"><br />\r\n</span></span> \r\n</p>\r\n<img src=\"http://201106.image.myqcloud.com/201106/0/c6556715-ea4e-46e8-a8a3-cc190aab63bf/original\" width=\"744\" height=\"484\" alt=\"\" /> \r\n<p>\r\n	<br />\r\n</p>\r\n<p class=\"MsoListParagraph\" style=\"margin-left:18.0pt;text-indent:-18.0pt;\">\r\n	<span><span style=\"font-family:\'Microsoft YaHei\';font-size:14px;\">2、</span><span style=\"font-size:7pt;line-height:normal;font-family:\'Times New Roman\';\"> </span></span><b><span style=\"font-size:14px;font-family:\'Microsoft YaHei\';color:#E53333;\">良心食品，无添加剂</span></b><span style=\"font-size:14px;font-family:\'Microsoft YaHei\';\">：</span><span style=\"font-size:14px;font-family:\'Microsoft YaHei\';\">宝岛金砖品牌始终</span><span style=\"font-size:14px;font-family:\'Microsoft YaHei\';color:#222222;\">秉承“品质为上，诚信第一”的经营宗旨，</span><span style=\"font-family:\'Microsoft YaHei\';font-size:14px;\">以质量为本，承诺所有产品无色素、无香精、无任何有害添加剂。</span><span style=\"font-size:10.5pt;font-family:微软雅黑, sans-serif;\"><span style=\"font-family:\'Microsoft YaHei\';\">通过精心配比的美味原料，还原食材本身的味道，阳光小麦和新鲜玫瑰花的完美邂逅，让充满花香的美味在舌尖绽放。</span></span> \r\n</p>\r\n<p class=\"MsoListParagraph\" style=\"margin-left:18.0pt;text-indent:-18.0pt;\">\r\n	<span style=\"font-size:10.5pt;font-family:微软雅黑, sans-serif;\"><span style=\"font-family:\'Microsoft YaHei\';\"><br />\r\n</span></span> \r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<img src=\"http://201106.image.myqcloud.com/201106/0/c21cd3d2-c4ca-4a59-bde2-d317419202f3/original\" width=\"615\" height=\"352\" alt=\"\" style=\"line-height:1.5;\" /> \r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<br />\r\n</p>\r\n<p class=\"MsoNormal\" style=\"margin-left:0cm;\">\r\n	<span style=\"font-family:微软雅黑, sans-serif;\"></span> \r\n</p>\r\n<p class=\"MsoListParagraph\" style=\"text-align:left;margin-left:18pt;text-indent:-18pt;\">\r\n	<span style=\"font-family:\'\';font-size:14px;\">3、</span><b><span style=\"font-size:14px;font-family:宋体;color:#E53333;\">品质保证</span></b><b><span style=\"font-size:14px;color:#E53333;\">&nbsp; </span></b><b><span style=\"font-size:14px;font-family:宋体;color:#E53333;\">包装精美</span></b><span style=\"font-size:14px;font-family:宋体;\">：</span><span style=\"font-size:10.5pt;font-family:微软雅黑, sans-serif;color:#333333;\"><span style=\"font-size:14px;\">鲜花饼的制作历史可追溯到</span><span style=\"font-size:14px;\">300</span><span style=\"font-size:14px;\">多年前的清代，由于鲜花饼颇受民众的喜爱而广为流传，并经朝内官员的进贡，使之一跃成为宫廷御点，深得</span></span><span><span style=\"font-size:14px;font-family:\'Microsoft YaHei\';color:#000000;\"><span style=\"font-size:14px;color:#000000;font-family:\'Microsoft YaHei\';\">乾隆</span></span></span><span style=\"font-size:14px;font-family:微软雅黑, sans-serif;color:#333333;\">皇帝的喜爱。在台湾，鲜花饼一直是内地游客作为伴手礼的最佳选择，因此时常处于脱销的状态。</span><span style=\"font-size:10.5pt;font-family:;\" \"=\"\"></span> \r\n</p>\r\n<p class=\"MsoListParagraph\" style=\"text-align:left;margin-left:18pt;text-indent:-18pt;\">\r\n	<span style=\"font-size:14px;font-family:微软雅黑, sans-serif;color:#333333;\"><br />\r\n</span> \r\n</p>\r\n<p class=\"MsoListParagraph\" style=\"text-align:left;margin-left:18pt;text-indent:-18pt;\">\r\n	<span style=\"font-size:14px;font-family:微软雅黑, sans-serif;color:#333333;\"><img src=\"http://201106.image.myqcloud.com/201106/0/f48d63f3-0fda-4e49-b918-bf39185c96ef/original\" width=\"720\" height=\"720\" alt=\"\" /><br />\r\n</span> \r\n</p>\r\n<p class=\"MsoListParagraph\" style=\"text-align:left;margin-left:18pt;text-indent:-18pt;\">\r\n	<br />\r\n</p>\r\n<p class=\"MsoNormal\" style=\"margin-left:0cm;\">\r\n	<b><span style=\"font-size:10.5pt;font-family:\'Microsoft YaHei\';\">4</span></b><b><span style=\"font-size:10.5pt;font-family:\'Microsoft YaHei\';\">、</span></b><b><span style=\"font-size:10.5pt;font-family:\'Microsoft YaHei\';color:#E53333;\">优良的售后服务</span></b><span style=\"font-size:10.5pt;font-family:\'Microsoft YaHei\';\">：</span><span style=\"font-size:10.5pt;font-family:\'Microsoft YaHei\';\">V</span><span style=\"font-size:10.5pt;font-family:\'Microsoft YaHei\';\">猫小店为广大的代理喵主提供了完善的售后服务，支持一件代发。推出新手开店扶持政策，开设专业培训课，让你在短时间内快速成长，迅速提升，针对你开店遇到的问题，专项分析，并且提供解决方案。</span><span style=\"font-size:9pt;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"margin-left:0cm;\">\r\n	<span style=\"font-size:9pt;font-family:\'Microsoft YaHei\';\">&nbsp;</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"margin-left:0cm;\">\r\n	<b><span style=\"font-size:13.5pt;font-family:\'Microsoft YaHei\';color:#E53333;\">心动了吗？赶快点击一键代理</span></b><b><span style=\"font-size:13.5pt;font-family:\'Microsoft YaHei\';color:#E53333;\">成为【宝岛金砖】代理人</span></b>\r\n</p>\r\n<p>\r\n	<a href=\"http://m.v-cat.cn/buyer/views/goods.html?type=1&shopId=${shopId}&productId=6e14bb9690b941e58f7957e52de1008d\"><img src=\"http://201106.image.myqcloud.com/201106/0/384c1401-797a-4ea8-9500-7f82bde03eb4/original\" width=\"640\" height=\"218\" alt=\"\" /></a>\r\n</p>\r\n<p>\r\n	<p class=\"MsoNormal\" style=\"margin-left:0cm;white-space:normal;\">\r\n		<b><span style=\"font-size:12pt;font-family:\'Microsoft YaHei\';color:#E53333;\">更多代理推荐，请关注</span></b><b><span style=\"font-size:12pt;color:#E53333;font-family:\'Microsoft YaHei\';\">V</span></b><b><span style=\"font-size:12pt;font-family:\'Microsoft YaHei\';color:#E53333;\">猫小店！</span></b>\r\n	</p>\r\n</p>', '', '', '1');
INSERT INTO `cms_article_data` VALUES ('27194a144a45470aba1237372970a9ff', '<p class=\"MsoNormal\" style=\"margin-left:0pt;text-indent:0pt;\">\r\n	<br />\r\n</p>\r\n<p class=\"MsoNormal\" style=\"margin-left:0pt;text-indent:0pt;\">\r\n	<br />\r\n</p>\r\n<p class=\"MsoNormal\" style=\"margin-left:0pt;text-indent:0pt;\">\r\n	<span style=\"font-family:微软雅黑;font-size:14px;color:#64451D;\">【V猫有话说】新手卖家怎么推广？这是广大V猫新手所面临的最大问题之一，如今的竞争很大，同行业有很多的竞争对手，作为一个新手你不要奢望开一个店，把宝贝上传好就会有人来买，俗话说“酒香也怕巷子深”，好的东西得让别人知道，我们的店铺介绍给别人，让别人知道，只有知道的人多了才会有人去浏览你的店从而产生购买。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"margin-left:0pt;text-indent:0pt;\">\r\n	<span style=\"font-family:微软雅黑;font-size:10.5pt;\"><br />\r\n</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"margin-left:0pt;text-indent:0pt;\">\r\n	<span style=\"font-family:微软雅黑;font-size:10.5pt;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"margin-left:0pt;text-indent:0pt;\">\r\n	<span style=\"font-family:微软雅黑;font-size:16px;\">在进行推广工作之前，首先你得先给自己的</span><span style=\"font-family:微软雅黑;font-size:16px;\">V猫小店</span><span style=\"font-family:微软雅黑;font-size:16px;\">定个位，做一个简单的推广方法，是想做店铺整推还是推广单品做爆款。然后给自己的目标消费群体定个位，你要推广的东西适用于那些人群，这些目标客户最多的平台是哪里。</span><span style=\"font-family:微软雅黑;font-size:10.5pt;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"margin-left:0pt;text-indent:0pt;\">\r\n	<span style=\"font-family:微软雅黑;font-size:16px;\"><br />\r\n</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"margin-left:0pt;text-indent:0pt;\">\r\n	<span style=\"font-family:微软雅黑;font-size:16px;\"><img src=\"http://201106.image.myqcloud.com/201106/0/8ed9fb05-a684-4da2-8743-eda29b6f5011/original\" width=\"1020\" height=\"644\" alt=\"\" /><br />\r\n</span> \r\n</p>\r\n<p class=\"p\">\r\n	<span style=\"font-family:微软雅黑;font-size:10.5pt;\"></span> \r\n</p>\r\n<p class=\"p\">\r\n	<span style=\"font-family:微软雅黑;font-size:16px;\"><br />\r\n</span> \r\n</p>\r\n<p class=\"p\">\r\n	<span style=\"font-family:微软雅黑;font-size:16px;\">如果是店铺，想一个好的店铺名字是很必要的。比如说，产品另类个性，可以结合网络热词，像“一家很拽的店”这种；或者专营某种特定商品，如“黄老五专营店”之类；再比如反其意行之，“厕所旁的美食小店”等等。</span><span style=\"font-family:微软雅黑;font-size:16px;\">其实在整个推广的过程中，</span><span style=\"font-family:微软雅黑;font-size:16px;\">店铺名字是</span><span style=\"font-family:微软雅黑;font-size:16px;\">平台发布</span><span style=\"font-family:微软雅黑;font-size:16px;\">在直观的“广告”</span><span style=\"font-family:微软雅黑;font-size:16px;\">，</span><span style=\"font-family:微软雅黑;font-size:16px;\">很容易吸引潜在用户的眼球和好奇心，这样</span><span style=\"font-family:微软雅黑;font-size:16px;\">明晃晃的广告</span><span style=\"font-family:微软雅黑;font-size:16px;\">效应</span><span style=\"font-family:微软雅黑;font-size:16px;\">给你带来的效果</span><span style=\"font-family:微软雅黑;font-size:16px;\">很可能让你想都想不到</span><span style=\"font-family:微软雅黑;font-size:16px;\">。</span><span style=\"font-family:微软雅黑;font-size:10.5pt;\"></span> \r\n</p>\r\n<p class=\"p\">\r\n	<span style=\"font-family:微软雅黑;font-size:10.5pt;\"><br />\r\n</span> \r\n</p>\r\n<p class=\"p\">\r\n	<span style=\"font-family:微软雅黑;font-size:10.5pt;\"></span> \r\n</p>\r\n<p class=\"p\">\r\n	<span style=\"font-family:微软雅黑;font-size:16px;\">如果是单品，</span><span style=\"font-family:微软雅黑;font-size:16px;\">正确的方法应该是把你的广告软化，进行发布，这样首先可以保证你的</span><span style=\"font-family:微软雅黑;font-size:16px;\">广告</span><span style=\"font-family:微软雅黑;font-size:16px;\">存活率，只有</span><span style=\"font-family:微软雅黑;font-size:16px;\">大家停留一秒，</span><span style=\"font-family:微软雅黑;font-size:16px;\">才会有机会让</span><span style=\"font-family:微软雅黑;font-size:16px;\">他点击进入你的</span><span style=\"font-family:微软雅黑;font-size:16px;\">V猫小店</span><span style=\"font-family:微软雅黑;font-size:16px;\">，才会有销售。目前软广告的普遍做法都是间接性的传递广告信息，一般是以一个使用者进行经验分享这种类型进行传递，这是最普通软广告做法。</span><span style=\"font-family:微软雅黑;font-size:16px;\">这也是很多</span><span style=\"font-family:微软雅黑;font-size:16px;\">优秀的V猫小店店主都比较喜欢的方式。</span><span style=\"font-family:微软雅黑;font-size:10.5pt;\"></span> \r\n</p>\r\n<p class=\"p\">\r\n	<span style=\"font-family:微软雅黑;font-size:10.5pt;\"><br />\r\n</span> \r\n</p>\r\n<p class=\"p\">\r\n	<span style=\"font-family:微软雅黑;font-size:10.5pt;\"></span> \r\n</p>\r\n<p class=\"p\">\r\n	<span style=\"font-family:微软雅黑;font-size:14px;color:#64451D;\">好了，本期本大湿就说这么多。新手注意了哦~</span><span style=\"font-family:微软雅黑;font-size:10.5pt;\"></span> \r\n</p>\r\n<p class=\"p\">\r\n	<span style=\"font-family:微软雅黑;font-size:16px;\"><br />\r\n</span> \r\n</p>\r\n<p class=\"p\">\r\n	<span style=\"font-family:微软雅黑;font-size:16px;\"><br />\r\n</span> \r\n</p>', '', '', '1');
