INSERT INTO `cms_article_data` VALUES ('2e4179f9252d4e32bb09b48788bc65cd', '<p>\r\n	<span style=\"font-size:14px;font-family:\'Microsoft YaHei\';\">&nbsp; &nbsp; &nbsp; &nbsp;<span style=\"color:#E53333;\"><strong>据V猫庄园汉源基地工作人员最新消息称，今天下午汉源</strong></span></span><span style=\"color:#333333;font-family:宋体;font-size:14px;line-height:25.2px;white-space:normal;background-color:#FFFFFF;\"><span style=\"font-size:14px;font-family:\'Microsoft YaHei\';color:#E53333;\"><strong>受局部强对流恶劣天气影响，遭受区域性暴雨冰雹袭击，</strong></span><span style=\"color:#E53333;font-family:\'Microsoft YaHei\';font-size:14px;line-height:25.2px;white-space:normal;background-color:#FFFFFF;\"><strong>致基地部分车厘子受损。具体</strong></span><span style=\"font-size:14px;font-family:\'Microsoft YaHei\';color:#E53333;\"><strong>受灾情况，微猫电商将派出小组实地勘察</strong></span><span style=\"font-size:14px;font-family:\'Microsoft YaHei\';color:#E53333;\"><strong>，核实果子产量后进行后续报道。</strong></span></span>\r\n</p>\r\n<p>\r\n	<span style=\"color:#E53333;font-family:\'Microsoft YaHei\';font-size:14px;line-height:25.2px;white-space:normal;background-color:#FFFFFF;\"><strong>&nbsp; &nbsp; &nbsp; &nbsp;针对以上情况，为保证本季有足够的车厘子供V猫店主销售，现暂时将“汉源·清溪车厘子”产品库存更改为0，待实地勘察小组反馈最新信息后进行调整，望店主们耐心等待。</strong></span>\r\n</p>\r\n<p>\r\n	<span style=\"color:#E53333;font-family:\'Microsoft YaHei\';font-size:14px;line-height:25.2px;white-space:normal;background-color:#FFFFFF;\"><strong>&nbsp; &nbsp; &nbsp; &nbsp;感谢各位店主对V猫庄园一如既往的支持，谢谢！</strong></span>\r\n</p>', '', '', '1');
