INSERT INTO `cms_article_data` VALUES ('3914951b1ff74d44883c89213be13228', '<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<span style=\"font-size:14px;font-family:&quot;\">亲爱的V猫店主：</span>\r\n</p>\r\n<p>\r\n	<span style=\"font-size:14px;font-family:&quot;\">第一批<span style=\"color:#E53333;\"><strong>【</strong></span><span style=\"color:#E53333;\"><strong>蒲江红心猕猴桃</strong></span><span style=\"color:#E53333;\"><strong>】</strong></span>已于昨日（<span style=\"color:#E53333;\"><strong>8月18日</strong></span>）全部发货，由于</span><span style=\"font-size:14px;font-family:&quot;\">天气炎热，气温过高，加上猕猴桃本身就是后熟类水果，水份流失速度比其他果子快，所以收到的果子总重量可能存在100g~200g的误差，请各位店主做好相应的售前售后工作。</span>\r\n</p>\r\n<p>\r\n	<span style=\"font-size:14px;font-family:&quot;\">感谢您的支持，谢谢！</span>\r\n</p>', '', '', '1');
