INSERT INTO `cms_article_data` VALUES ('477a408c0428455d8d051e04ba39e20e', '<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<span style=\"font-family:&quot;font-size:14px;\">亲爱的V猫店主：</span>\r\n</p>\r\n<p>\r\n	<span style=\"font-family:&quot;font-size:14px;\">三只馋猫和原本果子的买一送一活动即将在今日（11月14日）11：00结束，波斯贡枣和黄老五的低价拼团也将于今日14:00结束，望各位店主抓紧时间下单。</span>\r\n</p>\r\n<p>\r\n	<span style=\"font-family:&quot;font-size:14px;\">感谢您的支持，谢谢！</span>\r\n</p>', '', '', '1');
