INSERT INTO `cms_article_data` VALUES ('51258342052d419f8769b68d441501a5', '<p>\r\n	<img src=\"http://image.v-cat.cn/201106/0/6978a709-9e8b-4db7-b387-020f50a5810a/original\" width=\"700\" height=\"800\" alt=\"\" />\r\n</p>\r\n<p>\r\n	<span style=\"font-size:14px;font-family:&quot;\">V猫聚集的是一群真正的美食爱好者，我们倡导绿色、生态、健康的吃货方式，我们谨代表所有美食爱好者寻找原生态特色美食。以</span><span lang=\"EN-US\" style=\"font-size:14px;font-family:&quot;\">V</span><span style=\"font-size:14px;font-family:&quot;\">猫招商团队为基础，我们将打造一个有态度的“寻宝团”，分享美食发现之旅。</span>\r\n</p>\r\n<p>\r\n	<p class=\"MsoNormal\" align=\"left\" style=\"line-height:17.65pt;background:white;\">\r\n		<span style=\"font-size:14px;font-family:&quot;color:#E53333;\"><strong>壹</strong></span><span lang=\"EN-US\" style=\"font-size:14px;font-family:&quot;color:#E53333;\"><strong> | </strong></span><span style=\"font-size:14px;font-family:&quot;color:#E53333;\"><strong>活动细则</strong></span><span lang=\"EN-US\"><o:p></o:p></span>\r\n	</p>\r\n	<p class=\"MsoNormal\" align=\"left\" style=\"line-height:17.65pt;background:white;\">\r\n		<span style=\"font-size:14px;font-family:&quot;\">寻宝时间：</span><span lang=\"EN-US\" style=\"font-size:14px;font-family:&quot;\">2016</span><span style=\"font-size:14px;font-family:&quot;\">年</span><span lang=\"EN-US\" style=\"font-size:14px;font-family:&quot;\">10</span><span style=\"font-size:14px;font-family:&quot;\">月</span><span lang=\"EN-US\" style=\"font-size:14px;font-family:&quot;\">13</span><span style=\"font-size:14px;font-family:&quot;\">日（星期四）；</span><o:p></o:p>\r\n	</p>\r\n	<p class=\"MsoNormal\" align=\"left\" style=\"line-height:17.65pt;background:white;\">\r\n		<span style=\"font-size:14px;font-family:&quot;\">参与人数：</span><span lang=\"EN-US\" style=\"font-size:14px;font-family:&quot;\">50</span><span style=\"font-size:14px;font-family:&quot;\">人；</span><o:p></o:p>\r\n	</p>\r\n	<p class=\"MsoNormal\" align=\"left\" style=\"line-height:17.65pt;background:white;\">\r\n		<span style=\"font-size:14px;font-family:&quot;\">寻宝地点：资水河大闸蟹基地（崇州）；</span><o:p></o:p>\r\n	</p>\r\n	<p class=\"MsoNormal\" align=\"left\" style=\"line-height:17.65pt;background:white;\">\r\n		<span style=\"font-size:14px;font-family:&quot;\">参与权限：仅限四川用户；</span><o:p></o:p>\r\n	</p>\r\n	<p class=\"MsoNormal\" align=\"left\" style=\"line-height:17.65pt;background:white;\">\r\n		<span style=\"font-size:14px;font-family:&quot;\">活动安排：接触大自然采风，体验抓螃蟹、感受农家风情、坐游艇、吃海鲜大餐，</span><span lang=\"EN-US\" style=\"font-size:14px;font-family:&quot;\">DIY</span><span style=\"font-size:14px;font-family:&quot;\">螃蟹的吃法，品鉴有机农家菜参与互动活动游戏，了解关于正宗资水河大闸蟹的专业知识；</span><o:p></o:p>\r\n	</p>\r\n	<p class=\"MsoNormal\" align=\"left\" style=\"line-height:17.65pt;background:white;\">\r\n		<span style=\"font-size:14px;font-family:&quot;\">报名费用：</span><span lang=\"EN-US\" style=\"font-size:14px;font-family:&quot;\">50</span><span style=\"font-size:14px;font-family:&quot;\">元（原价</span><span lang=\"EN-US\" style=\"font-size:14px;font-family:&quot;\">480</span><span style=\"font-size:14px;font-family:&quot;\">元，含现场赠送资水河大闸蟹</span><span lang=\"EN-US\" style=\"font-size:14px;font-family:&quot;\">4</span><span style=\"font-size:14px;font-family:&quot;\">只装</span><span lang=\"EN-US\" style=\"font-size:14px;font-family:&quot;\">2</span><span style=\"font-size:14px;font-family:&quot;\">公</span><span lang=\"EN-US\" style=\"font-size:14px;font-family:&quot;\">/2</span><span style=\"font-size:14px;font-family:&quot;\">母、来回车旅费、午餐海鲜大餐）；</span><span lang=\"EN-US\"><o:p></o:p></span>\r\n	</p>\r\n	<p class=\"MsoNormal\" align=\"left\" style=\"line-height:17.65pt;background:white;\">\r\n		<span style=\"font-size:14px;font-family:&quot;\">报名截止时间：</span><span lang=\"EN-US\" style=\"font-size:14px;font-family:&quot;\">10</span><span style=\"font-size:14px;font-family:&quot;\">月</span><span lang=\"EN-US\" style=\"font-size:14px;font-family:&quot;\">12</span><span style=\"font-size:14px;font-family:&quot;\">日 </span><span lang=\"EN-US\" style=\"font-size:14px;font-family:&quot;\">21</span><span style=\"font-size:14px;font-family:&quot;\">点</span><o:p></o:p>\r\n	</p>\r\n	<p class=\"MsoNormal\" align=\"left\" style=\"line-height:17.65pt;background:white;\">\r\n		<span style=\"font-size:14px;font-family:&quot;\"><br />\r\n</span>\r\n	</p>\r\n	<p class=\"MsoNormal\" align=\"left\" style=\"line-height:17.65pt;background:white;\">\r\n		<span style=\"font-size:14px;font-family:&quot;color:#E53333;\"><strong>贰</strong></span><span lang=\"EN-US\" style=\"font-size:14px;font-family:&quot;color:#E53333;\"><strong> | </strong></span><span style=\"font-size:14px;font-family:&quot;color:#E53333;\"><strong>报名方式</strong></span><span lang=\"EN-US\"><o:p></o:p></span>\r\n	</p>\r\n	<p class=\"MsoListParagraph\" align=\"left\" style=\"margin-left:18pt;text-indent:-18pt;line-height:17.65pt;background:white;\">\r\n		<!--[if !supportLists]--><span style=\"font-size:14px;font-family:&quot;\">1、 </span><!--[endif]--><span style=\"font-size:14px;font-family:&quot;\">联系</span><span lang=\"EN-US\" style=\"font-size:14px;font-family:&quot;\">V</span><span style=\"font-size:14px;font-family:&quot;\">猫总管，直接微信付费后报名成功，付费后报名费不再退回。</span><span lang=\"EN-US\"><o:p></o:p></span>\r\n	</p>\r\n	<p class=\"MsoNormal\" align=\"left\" style=\"line-height:17.65pt;background:white;\">\r\n		<span style=\"font-size:14px;font-family:&quot;\">&nbsp;<img src=\"http://image.v-cat.cn/201106/0/5a7cc504-aeb2-4b91-a530-c42cb66d41ee/original\" width=\"465\" height=\"454\" alt=\"\" /></span>\r\n	</p>\r\n	<p class=\"MsoNormal\" align=\"left\" style=\"line-height:17.65pt;background:white;\">\r\n		<span style=\"font-size:14px;font-family:&quot;color:#E53333;\"><strong>叁</strong></span><span lang=\"EN-US\" style=\"font-size:14px;font-family:&quot;color:#E53333;\"><strong> | </strong></span><span style=\"font-size:14px;font-family:&quot;color:#E53333;\"><strong>活动内容</strong></span><span lang=\"EN-US\"><o:p></o:p></span>\r\n	</p>\r\n	<p class=\"MsoNormal\" align=\"left\" style=\"line-height:17.65pt;