INSERT INTO `cms_article_data` VALUES ('6d0e35201be241819d0632a4d19c74bc', '<p style=\"white-space:normal;\">\r\n	<span \"=\"\" style=\"font-size:14px;\"><br />\r\n</span>\r\n</p>\r\n<p style=\"white-space:normal;\">\r\n	<span \"=\"\" style=\"font-size:14px;\">亲爱的V猫店主：</span>\r\n</p>\r\n<p style=\"white-space:normal;\">\r\n	<span \"=\"\" style=\"font-size:14px;\"><span \"=\"\">双十一劲爆价格，<span style=\"font-size:14px;line-height:21px;white-space:normal;\">金都一号红心火龙果&nbsp;</span></span><span \"=\"\">将于今日（11月4日）17：20加入【双十一拼团</span></span><span \"=\"\" style=\"font-size:14px;\">】，该活动只持续到本周末（11月6日），下周一（11月7日）将恢复原价，以下是产品信息预览：</span>\r\n</p>\r\n<p style=\"white-space:normal;\">\r\n	<span \"=\"\" style=\"font-size:14px;\"><span \"=\"\">产品名称：【双十一拼团】</span><span \"=\"\">金都一号红心火龙果 3</span><span \"=\"\">人团</span></span>\r\n</p>\r\n<p style=\"white-space:normal;\">\r\n	<span \"=\"\" style=\"font-size:14px;\">产品规格：5斤装</span>\r\n</p>\r\n<p style=\"white-space:normal;\">\r\n	<span \"=\"\" style=\"font-size:14px;\">拼团价格：39.9元（原价49.9元）</span>\r\n</p>\r\n<p style=\"white-space:normal;\">\r\n	<span \"=\"\" style=\"font-size:14px;\">白金店主利润：8元</span>\r\n</p>\r\n<p style=\"white-space:normal;\">\r\n	<span \"=\"\" style=\"font-size:14px;\">特约店主利润：6元</span>\r\n</p>\r\n<p style=\"white-space:normal;\">\r\n	<span \"=\"\" style=\"font-size:14px;\">欢迎店主代理销售，</span><span \"=\"\" style=\"font-size:14px;\">双十一期间V猫将源源不断的为大家提供新的活动产品，持续关注哟！</span>\r\n</p>', '', '', '1');
INSERT INTO `cms_article_data` VALUES ('6d43a0335f2a45a0bd1f6b6bdb2312cb', '<p>\r\n	<span style=\"font-family:\" font-size:16px;\"=\"\"><span style=\"color:#E53333;line-height:2;font-size:14px;font-family:&quot;\" \"=\"\"><strong>温馨提示</strong></span><span style=\"line-height:2;font-size:14px;font-family:&quot;\" \"=\"\">：收到芒果的朋友们，请按照正确方法催熟，才能尽快吃到香甜美味的芒果</span></span>\r\n</p>\r\n<p>\r\n	<span microsoft=\"\" yahei\";=\"\" line-height:=\"\" 2;\"=\"\" yahei\";\"=\"\" line-height:2;\"=\"\" style=\"color: rgb(229, 51, 51); font-size: 14px; font-family: &quot;Microsoft YaHei&quot;;\"><strong>Step1:</strong></span><span microsoft=\"\" yahei\";=\"\" line-height:=\"\" 2;\"=\"\" yahei\";\"=\"\" line-height:2;\"=\"\" style=\"font-size: 14px; font-family: &quot;Microsoft YaHei&quot;;\">首先打开箱子，把套芒果的网套（运输过程中的保护层）去掉，因为网套不透气，易捂坏果；</span>\r\n</p>\r\n<p>\r\n	<span style=\"font-family:\" font-size:16px;line-height:2;\"=\"\"><span style=\"color: rgb(229, 51, 51); font-size: 14px; font-family: \" microsoft=\"\" yahei\";\"=\"\" line-height:2;\"=\"\"><strong><span style=\"line-height:2;font-family:&quot;\">S</span><span style=\"line-height:2;font-family:&quot;\">tep2:</span></strong></span><span style=\"font-size: 14px; font-family: \" microsoft=\"\" yahei\";\"=\"\" line-height:2;\"=\"\"><span style=\"line-height:2;font-family:&quot;\">用纸巾将每个芒果上的水渍和汁液擦干净，避免水分和汁液侵蚀果皮;导致发黑变烂；</span><span style=\"line-height:2;\"></span></span></span> \r\n</p>\r\n<p>\r\n	<span style=\"font-family:\" font-size:16px;line-height:2;\"=\"\"><strong><span style=\"color: rgb(229, 51, 51); font-size: 14px; font-family: &quot;Microsoft YaHei&quot;;\" microsoft=\"\" yahei\";=\"\" line-height:=\"\" 2;\"=\"\" yahei\";\"=\"\" line-height:2;\"=\"\">Step3:</span></strong><span style=\"font-size: 14px; font-family: &quot;Microsoft YaHei&quot;;\" microsoft=\"\" yahei\";=\"\" line-height:=\"\" 2;\"=\"\" yahei\";\"=\"\" line-height:2;\"=\"\">用纸分别把每个芒果包起来放回纸箱中，盖上盖子。所有芒果放在一个箱里，它们互相产生热量，芒果成熟需要的高温环境。由于没放催熟剂，等到自然熟的时间就要长一点；</span></span>\r\n</p>\r\n<p>\r\n	<span style=\"font-family:\" font-size:16px;line-height:2;\"=\"\"><strong><span style=\"color: rgb(229, 51, 51); font-size: 14px; font-family: &quot;Microsoft YaHei&quot;;\" microsoft=\"\" yahei\";=\"\" line-height:=\"\" 2;\"=\"\" yahei\";\"=\"\" line-height:2;\"=\"\">Step4:</span></strong><span style=\"font-size: 14px; font-family: &quot;Microsoft YaHei&quot;;\" microsoft=\"\" yahei\";=\"\" line-height:=\"\" 2;\"=\"\" yahei\";\"=\"\" line-height:2;\"=\"\">要想熟得更快，就把纸箱里放入苹果和香蕉，它们会散发出天然植物激素“乙烯“，它是种成熟激素，可自然催熟芒果；</span></span>\r\n</p>\r\n<p>\r\n	<span style=\"font-family:\" font-size:16px;line-height:2;\"=\"\"><strong><span style=\"color: rgb(229, 51, 51); font-size: 14px; font-family: &quot;Microsoft YaHei&quot;;\" microsoft=\"\" yahei\";=\"\" line-height:=\"\" 2;\"=\"\" yahei\";\"=\"\" line-height:2;\"=\"\">Step5:</span></strong><span style=\"font-size: 14px; font-family: &quot;Microsoft YaHei&quot;;\" microsoft=\"\" yahei\";=\"\" line-height:=\"\" 2;\"=\"\" yahei\";\"=\"\" line-height:2;\"=\"\">静待果熟，每隔2-3天检查一下成熟度，记住不看颜色，耙了有果香味了就吃！越香越甜！皮越皱巴也越甜；</span></span> \r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<span style=\"font-size: 14px; font-family: &quot;Microsoft YaHei&quot;;\" microsoft=\"\" yahei\";\"=\"\" yahei\";=\"\" line-height:=\"\" 2;\"=\"\" font-size:16px;line-height:2;\"=\"\">特别提醒：如果熟多了吃不完，可以放冰箱保鲜盒，生的千万不要放冰箱，要不然永远不会熟只会烂掉。</span><br />', '', '', '1');
INSERT INTO `cms_article_data` VALUES ('6ee606285a92453baed9b7996e9d7994', '<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<img src=\"http://image.v-cat.cn/201106/0/4a3a5f44-5d29-4932-b035-04f3564d81fa/original\" width=\"4226\" height=\"2272\" alt=\"\" />\r\n</p>', '', '', '1');
INSERT INTO `cms_article_data` VALUES ('71c71e8956574f0f9c58c5dca2ff6961', '<p>\r\n	<br />\r\n</p>\r\n<p style=\"margin-left:0cm;\">\r\n	<br />\r\n</p>\r\n<p style=\"margin-left:0cm;\">\r\n	<span style=\"line-height:1.5;font-size:14px;\"><strong>关于【V猫直邮】和供应商发货</strong><strong>：</strong></span> \r\n</p>\r\n<p style=\"margin-left:0cm;\">\r\n	<span style=\"line-height:1.5;font-size:14px;\"><span style=\"font-size:14px;\">优化后的</span></span><span style=\"line-height:1.5;font-size:14px;\">V</span><span style=\"line-height:1.5;font-size:14px;\">猫小店将有</span><span style=\"color:#E53333;line-height:1.5;font-size:14px;\">两种物流模式</span><span style=\"line-height:1.5;font-size:14px;\">：</span><span style=\"line-height:1.5;font-size:14px;\">其一</span><span style=\"line-height:1.5;font-size:14px;\">，</span><span style=\"font-size:14px;\">V</span><span style=\"font-size:14px;\"><span style=\"font-size:14px;\">猫小店特开通</span><span style=\"color:#E53333;font-size:14px;\">【</span></span><span style=\"font-size:14px;color:#E53333;\">V</span><span style=\"font-size:14px;\"><span style=\"color:#E53333;font-size:14px;\">猫直邮】</span><span style=\"font-size:14px;\">业务，通过“独立建仓”的方式，严格保证产品质量及提高物流效率的同时，降低喵主卖货的物流成本。</span></span> \r\n</p>\r\n<p style=\"margin-left:0cm;\">\r\n	<span style=\"font-size:14px;\">【</span><span style=\"font-size:14px;\">V</span><span style=\"font-size:14px;\"><span style=\"font-size:14px;\">猫直邮】产品</span><span style=\"color:#E53333;font-size:14px;\">以平台内定期主推的代理产品以及热门产品为主</span><span style=\"font-size:14px;\">。建仓后，消费者订单由</span></span><span style=\"font-size:14px;\">V</span><span style=\"font-size:14px;\">猫仓库受理，产品将直接从仓库发出。</span> \r\n</p>\r\n<p style=\"margin-left:0cm;\">\r\n	<img src=\"http://201106.image.myqcloud.com/201106/0/15163d01-149d-41b3-a0fe-9d04eb350986/original\" width=\"640\" height=\"330\" alt=\"\" /> \r\n</p>\r\n<p style=\"margin-left:0cm;\">\r\n	<span style=\"font-size:14px;\">其二，</span><span style=\"font-size:14px;line-height:1.5;\">消费者成功下单后，</span><span style=\"font-size:14px;line-height:1.5;color:#E53333;\">由原产地现摘后进行配送</span><span style=\"font-size:14px;line-height:1.5;\">。配送主要</span><span style=\"font-size:14px;line-height:1.5;color:#E53333;\">以生鲜、预售类产品为主</span><span style=\"font-size:14px;line-height:1.5;\">，由于该类产品时效性强，因此为保证产品质量，喵主在代理卖货时应注意供货范围。</span> \r\n</p>\r\n<p style=\"margin-left:0cm;\">\r\n	<span style=\"font-size:14px;line-height:1.5;\"><br />\r\n</span> \r\n</p>\r\n<p style=\"margin-left:0cm;\">\r\n	<span style=\"font-size:14px;line-height:1.5;\"><img src=\"http://201106.image.myqcloud.com/201106/0/9d76d11c-8cbc-4998-bf8e-02f8c315c546/original\" width=\"500\" height=\"370\" alt=\"\" /><br />\r\n</span> \r\n</p>\r\n<p style=\"margin-left:0cm;\">\r\n	<span></span> \r\n</p>\r\n<p style=\"margin-left:0cm;\">\r\n	<br />\r\n</p>\r\n<p style=\"margin-left:0cm;\">\r\n	<span style=\"line-height:1.5;font-size:14px;\"><strong>关于【合并订单】：</strong></span> \r\n</p>\r\n<p style=\"margin-left:0cm;\">\r\n	<span style=\"line-height:1.5;font-size:14px;\">喵主购买产品后，可将配送同一地址的产品放入购物车</span><strong><span style=\"line-height:1.5;font-size:14px;color:#E53333;\">统一结算即可合并订单</span></strong><span style=\"line-height:1.5;font-size:14px;\">，该订单一律</span><strong><span style=\"line-height:1.5;font-size:14px;color:#E53333;\">按照重量计费</span></strong><span style=\"line-height:1.5;font-size:14px;\">（</span><span style=\"line-height:1.5;font-size:14px;\">1</span><span style=\"line-height:1.5;font-size:14px;\">公斤以内按照首重</span><span style=\"line-height:1.5;font-size:14px;\">6</span><span style=\"line-height:1.5;font-size:14px;\">元起计费）。</span><span></span> \r\n</p>\r\n<p style=\"margin-left:0cm;\">\r\n	<span style=\"font-size:14px;line-height:1.5;\">区别于单个订单按件收费，经过合并订单后的产品统一打包配送，将大大节省物流费用。因此，对于热门类产品，喵主可利用</span><span style=\"font-size:14px;line-height:1.5;color:#E53333;\">合并订单进行囤货，以此来提高产品销售利润</span><span style=\"font-size:14px;line-height:1.5;\">。</span> \r\n</p>\r\n<p style=\"margin-left:0cm;\">\r\n	<br />\r\n</p>\r\n<p style=\"margin-left:0cm;\">\r\n	<img src=\"http://201106.image.myqcloud.com/201106/0/9edb898d-df4d-44ba-a7b4-5e2ca073d951/original\" width=\"640\" height=\"330\" alt=\"\" style=\"font-size:14px;line-height:1.5;\" /> \r\n</p>\r\n<p style=\"margin-left:0cm;\">\r\n	<strong><span style=\"line-height:1.5;font-size:14px;\"><br />\r\n</span></strong> \r\n</p>\r\n<p style=\"margin-left:0cm;\">\r\n	<strong><span style=\"line-height:1.5;font-size:14px;\">举个例子：</span></strong><span style=\"line-height:1.5;font-size:14px;\">喵主购买【陈记一菇】盒装250克，按照单个订单发货将产生最低6元的物流费用，而通过合并订单后，按照产品重量计算，喵主购买4盒【陈记一菇】盒装250克才仅需6元邮费。</span> \r\n</p>\r\n<p style=\"margin-left:0cm;\">\r\n	<span style=\"line-height:1.5;font-size:14px;\"><br />\r\n</span> \r\n</p>\r\n<p style=\"margin-left:0cm;\">\r\n	<strong><span style=\"line-height:1.5;font-size:14px;\">》》》》》</span></strong> \r\n</p>\r\n<p style=\"margin-left:0cm;\">\r\n	<strong><span style=\"line-height:1.5;font-size:14px;\">详情咨询：【</span><span style=\"line-height:1.5;font-size:14px;\">V</span><span style=\"line-height:1.5;font-size:14px;\">猫小店】客服电话028-62695971</span></strong> \r\n</p>\r\n<p style=\"margin-left:0cm;\">\r\n	<strong><span style=\"line-height:1.5;font-size:14px;\"><br />\r\n</span></strong> \r\n</p>', '', '', '1');
INSERT INTO `cms_article_data` VALUES ('72619b3ef3f64bfcaa3fc4bbc432a13f', '<p class=\"MsoNormal\" style=\"white-space:normal;\">\r\n	亲爱的V猫店主：<br />\r\nV猫庄园又为V猫店主上新品啦！\r\n</p>\r\n<p class=\"MsoNormal\" style=\"white-space:normal;\">\r\n	将于（3月30日）17:00新上架一款地方特色水果：丹棱红玉血橙！以下是产品信息预览：<br />\r\n产品名称：【拼团】丹棱红玉血橙<br />\r\n产品规格：5斤 &nbsp;单果果径约60-70mm<br />\r\n单品价格：36.8元\r\n</p>\r\n<p class=\"MsoNormal\" style=\"white-space:normal;\">\r\n	<span style=\"line-height:1.5;\">拼团价格：32.8元</span>\r\n</p>\r\n<p class=\"MsoNormal\" style=\"white-space:normal;\">\r\n	白金店主利润：8元<br />\r\n特约店主利润：6元\r\n</p>\r\n<p class=\"MsoNormal\" style=\"white-space:normal;\">\r\n	<br />\r\n</p>\r\n<p class=\"p\" style=\"margin-left:0pt;text-indent:0pt;white-space:normal;\">\r\n	<span style=\"line-height:1.5;\">欢迎店主代理销售，新的活动产品正在筹备中，敬请期待！</span>\r\n</p>', '', '', '1');
INSERT INTO `cms_article_data` VALUES ('727f7da48e7c456a9a695f9c841de95e', '<p class=\"MsoNormal\">\r\n	<span style=\"font-size:16px;font-family:\'Microsoft YaHei\';\"><br />\r\n</span> \r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<span style=\"font-size:16px;font-family:\'Microsoft YaHei\';\"><span style=\"line-height:1;\"><br />\r\n</span></span> \r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<span style=\"font-size:16px;font-family:\'Microsoft YaHei\';\"><span style=\"line-height:1.5;\"></span><span style=\"line-height:1.5;\">快来加入V猫，手机免费开店。0资金？0货源？想开店，就来V猫，手机开店你就是老板！V猫一小步，人生一大步。</span></span> \r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<span style=\"font-size:16px;font-family:\'Microsoft YaHei\';\"><span style=\"line-height:1.5;\"><br />\r\n</span></span> \r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<span style=\"font-size:16px;font-family:\'Microsoft YaHei\';line-height:1.5;\"> </span> \r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<span style=\"font-size:16px;font-family:\'Microsoft YaHei\';line-height:1.5;color:#E53333;\"><strong>【V猫小店APP】</strong></span> \r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<span style=\"font-size:16px;font-family:\'Microsoft YaHei\';line-height:1.5;color:#E53333;\"><strong><br />\r\n</strong></span> \r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<span style=\"font-size:16px;font-family:\'Microsoft YaHei\';line-height:1.5;\">首先，我要告诉你V猫小店APP是什么？</span> \r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<span style=\"font-size:16px;font-family:\'Microsoft YaHei\';line-height:1.5;\">一、一款免费开店，邀请好友还能各得30元开店大礼的神器；</span> \r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<span style=\"font-size:16px;font-family:\'Microsoft YaHei\';line-height:1.5;\">二、一个坐拥优质货源，销售利润丰厚，轻松开店赚钱的强大平台；</span> \r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<span style=\"font-size:16px;font-family:\'Microsoft YaHei\';line-height:1.5;\">三、一次组建分销战队，成就商界行业领袖梦想的机会；</span> \r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<span style=\"font-size:16px;font-family:\'Microsoft YaHei\';line-height:1.5;\">……</span> \r\n</p>\r\n<p class=\"MsoNormal\">\r