INSERT INTO `cms_article_data` VALUES ('8f23b2c57cc5456ab82e818278417661', '<p class=\"MsoNormal\">\r\n	<span class=\"c-gray\" style=\"font-size:12px;\">瑟伯与怀特说，</span><span style=\"font-size:12px;\">爱情有着奇妙的魔力，它使一个人为另一个人所倾倒。</span><span class=\"apple-converted-space\" style=\"font-size:12px;\">&nbsp;</span><span style=\"font-size:12px;\">所以有人总结道，爱情是使人变得盲目的东西，对于这一点我并不完全赞同，我认为它同样也能让人变得美好。正如</span><span class=\"apple-converted-space\" style=\"font-size:12px;\">那些能站在白马王子身边的女性，她们的确都具备着公主的气质，所以说要想遇到美好的人就必须要先让自己美好起来。今天给大家推荐的是艾琳睫毛增长液。</span> \r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<span class=\"apple-converted-space\"><br />\r\n</span> \r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<span class=\"apple-converted-space\"><img src=\"http://201106.image.myqcloud.com/201106/0/323db8f6-77fb-40c6-a8b6-5f4c7d9456a6/original\" width=\"377\" height=\"330\" alt=\"\" /><br />\r\n</span> \r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<span class=\"apple-converted-space\"><br />\r\n</span> \r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<span class=\"apple-converted-space\"> </span> \r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<span class=\"apple-converted-space\" style=\"line-height:1.5;\">产品包装使用白色的礼盒很有质感，并且卖家考虑的很周全，除了产品，还赠送了笔刷、睫毛尺，不仅使用方便也更加干净卫生。</span><span class=\"apple-converted-space\"><o:p></o:p></span> \r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<span class=\"apple-converted-space\"><br />\r\n</span> \r\n</p>\r\n<p>\r\n	<img src=\"http://201106.image.myqcloud.com/201106/0/186939e7-264e-494b-96e7-72c3c9b14c28/original\" width=\"434\" height=\"330\" alt=\"\" /> \r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<span class=\"apple-converted-space\"> </span> \r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<span class=\"apple-converted-space\" style=\"line-height:1.5;\">说说使用情况，睫毛液是植物原液提取，里面还有中草药的成分，比如当归。中草药和植物的组合让睫毛液透着淡淡的清香味，使用起来也并无刺激的感觉。</span><span class=\"apple-converted-space\"><o:p></o:p></span> \r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<span class=\"apple-converted-space\"><br />\r\n</span> \r\n</p>\r\n<img src=\"http://201106.image.myqcloud.com/201106/0/a7577019-f7bc-4819-845b-36609c51c8a5/original\" width=\"247\" height=\"330\" alt=\"\" /><br />\r\n<p>\r\n	<br />\r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<span class=\"apple-converted-space\"><img src=\"http://201106.image.myqcloud.com/201106/0/b379452e-e946-4bdc-b9c3-06a26e9296be/original\" width=\"440\" height=\"330\" alt=\"\" /><br />\r\n</span> \r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<span class=\"apple-converted-space\"><br />\r\n</span> \r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<span class=\"apple-converted-space\"> </span> \r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<span class=\"apple-converted-space\" style=\"line-height:1.5;\">现在有很多女性都会为了睫毛增长选择嫁接睫毛，但这种方式不仅成本投入高，反而还会损伤毛囊。与其着急一时还不如通过使用用药的方式，让睫毛自然生长，这样也能一直持续保持下去。</span><span style=\"line-height:1.5;\">提醒大家</span><span class=\"apple-converted-space\" style=\"line-height:1.5;\">为了看到效果，</span><span style=\"line-height:1.5;\">使用之前可以先测量一下睫毛的长度，这样方便做对比。每天晚上洁面之后就可以涂抹在睫毛根上了效果最好，尽管纯中药的配方不会在使用之后让眼睛出现血丝，但最好还是小心一点不要弄到眼睑和眼角处。</span> \r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<br />\r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<img src=\"http://201106.image.myqcloud.com/201106/0/f3b1cb6a-6dbc-49a1-bb06-d32aba2bb2d6/original\" width=\"247\" height=\"300\" alt=\"\" /> \r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<br />\r\n</p>\r\n<p>\r\n	<strong style=\"line-height:14.65625px;white-space:normal;\"><span style=\"background-color:#FFFFFF;\"><span style=\"line-height:1.5;\"><strong style=\"line-height:14.65625px;\"><span style=\"color:#E53333;font-size:16px;\">》》》》</span></strong></span></span></strong> \r\n</p>\r\n<p>\r\n	<strong style=\"line-height:14.65625px;white-space:normal;\"><span style=\"background-color:#FFFFFF;\"><span style=\"line-height:1.5;\"><strong style=\"line-height:14.65625px;\"><span style=\"color:#E53333;font-size:16px;\"><br />\r\n</span></strong></span></span></strong> \r\n</p>\r\n<p>\r\n	<strong style=\"line-height:14.65625px;white-space:normal;\"><span style=\"background-color:#FFFFFF;\"><span style=\"line-height:1.5;\"><strong style=\"line-height:14.65625px;\"><span style=\"color:#E53333;font-size:16px;\">如需代理或购买，喵主可前往找货源</span><span style=\"color:#E53333;font-size:16px;\">【美妆专区】</span></strong></span></span></strong> \r\n</p>\r\n<p>\r\n	<strong style=\"line-height:14.65625px;white-space:normal;\"><span style=\"background-color:#FFFFFF;\"><span style=\"line-height:1.5;\"><strong style=\"line-height:14.65625px;\"><span style=\"color:#E53333;font-size:16px;\"><br />\r\n</span></strong></span></span></strong> \r\n</p>\r\n<p>\r\n	<br />\r\n</p>', '', '', '1');
INSERT INTO `cms_article_data` VALUES ('9000cc51356b45049bedb475e2adc14e', '<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<span style=\"font-size:14px;font-family:&quot;\"></span><span style=\"font-size:14px;font-family:&quot;\">亲爱的V猫店主：</span>\r\n</p>\r\n<p>\r\n	<span style=\"font-size:14px;font-family:&quot;\">由于秋白桃库存售罄，现已下架，请店主知悉。</span>\r\n</p>\r\n<p>\r\n	<span style=\"font-size:14px;font-family:&quot;\">感谢您的支持，谢谢！</span>\r\n</p>', '', '', '1');
INSERT INTO `cms_article_data` VALUES ('901bcb781c3b4a918d778129ef0981d5', '<p class=\"MsoNormal\">\r\n	<br />\r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<span style=\"font-size:14px;line-height:1.5;\"><span style=\"line-height:2;\">大家都知道，</span><span style=\"color:#E53333;line-height:2;\">卸妆是护肤很重要的一步</span><span style=\"line-height:2;\">！脸部的清洁与否直接能影响到我们的肌肤状况！</span></span> \r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<span style=\"font-size:14px;line-height:2;\">因为每当我们的肌肤裸露在外时，就会被空气中的灰尘而</span><span style=\"font-size:14px;line-height:2;color:#E53333;\">堵塞毛孔</span><span style=\"font-size:14px;line-height:2;\">，</span><span style=\"font-size:14px;line-height:2;color:#E53333;\">出现</span><span style=\"font-size:14px;line-height:2;color:#E53333;\">长痘，长黑头、粉刺</span><span style=\"font-size:14px;line-height:2;\">等情况。</span> \r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<span style=\"font-size:14px;line-height:1.5;\"><span style=\"color:#E53333;\"><span style=\"line-height:2;\">个人建议</span><span style=\"color:#000000;line-height:2;\">：</span></span><span style=\"line-height:2;\">无论是化妆还是</span></span><span style=\"font-size:14px;line-height:2;\">涂防晒霜</span><span style=\"font-size:14px;line-height:2;\">、隔离霜、</span><span style=\"font-size:14px;line-height:2;\">BB</span><span style=\"font-size:14px;line-height:2;\">霜，即使是只涂了日常护肤品，经过一天的折腾最好还是要卸妆。</span> \r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<br />\r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<img src=\"http://201106.image.myqcloud.com/201106/0/c89d16e4-7618-418e-8ecb-fda78c6f0788/original\" width=\"456\" height=\"304\" alt=\"\" /> \r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<br />\r\n</p>\r\n<p class=\"MsoNormal\" style=\"line-height:11.0pt;\">\r\n	<span style=\"font-size:14px;line-height:1.5;\">本人是中性肌肤，个人比较喜欢使用洁肤油。所以，这次选择了来自台湾的【薇瑟芬幻采净透洁肤油】。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"line-height:11.0pt;\">\r\n	<span style=\"font-size:14px;line-height:1.5;\"><br />\r\n</span> \r\n</p>\r\n<p>\r\n	<img src=\"http://201106.image.myqcloud.com/201106/0/479a66a0-34f8-40a9-bdff-682617bc9088/original\" width=\"900\" height=\"1648\" alt=\"\" /> \r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<span style=\"font-size:14px;\"><span style=\"line-height:2;\">首先本身台湾的护肤品一直有着较好的口碑，并且这款产品本身</span><span style=\"color:#E53333;\"><span style=\"line-height:2;\">产地就在台湾，</span><span style=\"color:#000000;line-height:2;\">所以还是决定尝试一下</span></span><span style=\"line-height:2;\">。</span></span> \r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<span style=\"font-size:14px;line-height:2;\">在<span style=\"color:#E53333;\">包装上</span>，【薇瑟芬幻采净透洁肤油】采用的是纯白色和透明的瓶身，一股典型的台湾小清新风，简洁大气。</span> \r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<span style=\"font-size:14px;\"><span style=\"line-height:2;\">而且产品介绍很专业，附上了</span><span style=\"color:#E53333;line-height:2;\">中英文说明，显得很有档次呢</span><span style=\"line-height:2;\">！生产日期和保质期一应俱全。</span></span> \r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<span style=\"font-size:14px;line-height:2;\">个人感觉品牌定位应该属于</span><span style=\"font-size:14px;color:#E53333;line-height:2;\">中高端产品</span><span style=\"font-size:14px;line-height:2;\">。</span> \r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<span style=\"font-size:14px;\"><span style=\"line-height:2.5;\"></span><br />\r\n</span> \r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<img src=\"http://201106.image.myqcloud.com/201106/0/2455f2b1-962a-4db6-b9cb-bb6da64a0701/original\" width=\"900\" height=\"1326\" alt=\"\" /> \r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<img src=\"http://201106.image.myqcloud.com/201106/0/80e70201-0d73-4e94-ad0f-89b7194a0545/original\" width=\"900\" height=\"1363\" alt=\"\" /> \r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<img src=\"http://201106.image.myqcloud.com/201106/0/82abf4f8-a398-4f53-bd01-f0403037d52b/original\" width=\"900\" height=\"1270\" alt=\"\" style=\"font-size:12px;line-height:1.5;\" /> \r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<br />\r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<span style=\"font-size:14px;line-height:1.5;\">打开包装，如品牌所说，<span style=\"font-size:14px;line-height:21px;white-space:normal;\"><span style=\"color:#E53333;\">不添加任何色素</span>，</span>透过瓶子看见的是<span style=\"color:#E53333;\">淡黄趋于透明</span>的油状质地。</span><span></span> \r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<br />\r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<img src=\"http://201106.image.myqcloud.com/201106/0/a2b117d7-7c11-4287-af86-c32dacd2c0b0/original\" width=\"900\" height=\"1600\" alt=\"\" /> \r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<br />\r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<span style=\"font-size:14px;line-height:1.5;\"><span style=\"line-height:2;\">瓶口处贴心设计了</span><span style=\"color:#E53333;line-height:2;\">小夹子</span><span style=\"line-height:2;\">，</span><span style=\"color:#E53333;line-height:2;\">防止洁肤油流出</span><span style=\"line-height:2;\">，</span><span style=\"color:#E53333;line-height:2;\">方便携带</span><span style=\"line-height:2;\">。</span></span> \r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<span style=\"font-size:14px;line-height:1.5;\"><span style=\"color:#E53333;line-height:2;\">使用方便</span><span style=\"line-height:1.5;\"><span style=\"line-height:2;\">，顶部按压处还专门设计成了</span><span style=\"color:#E53333;\"><span style=\"line-height:2;\">手指按压凹槽形。</span><span style=\"font-size:14px;line-height:2;white-space:normal;color:#000000;\">圆润的瓶身非常精致，</span></span><span style=\"line-height:2;\">细节设计处尽显产品质感。</span></span></span><span></span> \r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<br />\r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<img src=\"http://201106.image.myqcloud.com/201106/0/224d6380-b780-4237-ba36-8a08d2cb4006/original\" width=\"900\" height=\"1496\" alt=\"\" /> \r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<br />\r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-align:center;\">\r\n	<strong><span style=\"color:#E53333;font-size:14px;line-height:1.5;\">————体验区————</span></strong> \r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<br />\r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<img src=\"http://201106.image.myqcloud.com/201106/0/475c933b-fec6-4773-bd9f-84951a5ec3dc/original\" width=\"900\" height=\"1089\" alt=\"\" /> \r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<br />\r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<span style=\"font-size:14px;line-height:1.5;\">体验开始了，手上画了眼线膏、唇膏</span><span style=\"font-size:14px;line-height:1.5;\">口红，记得这些都是防水性较高的彩妆哦！</span><span></span> \r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<br />\r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<img src=\"http://201106.image.myqcloud.com/201106/0/51831260-d9f0-489a-8067-293b45dd13a0/original\" width=\"900\" height=\"997\" alt=\"\" /> \r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<br />\r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<span style=\"font-size:14px;line-height:1.5;\">把洁肤油挤到卸妆棉上，轻轻一擦，可以看见唇彩</span><span style=\"font-size:14px;line-height:1.5;\">部分完全溶解了！卸妆效果果然强大哈！</span><span></span> \r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<br />\r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<img src=\"http://201106.image.myqcloud.com/201106/0/582bcde9-1974-4602-ac41-c6134031c6a2/original\" width=\"900\" height=\"715\" alt=\"\" /> \r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<br />\r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<span style=\"font-size:14px;\"><span style=\"line-height:2;\">一点都不夸张，来回擦拭了</span><span style=\"color:#E53333;line-height:2;\">两遍就基本上</span><span style=\"line-height:2;\">把下面的口红和眼线膏</span><span style=\"color:#E53333;line-height:1.5;\"><span style=\"line-height:2;\">全部擦干净了。</span><span style=\"color:#000000;line-height:2;\">卸的炒鸡干净！</span><span style=\"color:#000000;font-size:14px;line-height:2;white-space:normal;\">卸的炒鸡干净！</span><span style=\"color:#000000;font-size:14px;line-height:2;white-space:normal;\">卸的炒鸡干净！（重要的事情说三遍）</span><span style=\"color:#000000;\"></span><span style=\"color:#000000;\"></span></span></span> \r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<span style=\"font-size:14px;line-height:1.5;\"><span style=\"line-height:2;\">除了卸妆效果好，这款卸妆油相比以前用的其他油质卸妆油，</span><span style=\"color:#E53333;line-height:2;\">亲肤感更强</span><span style=\"line-height:2;\">，</span><span style=\"color:#E53333;line-height:2;\">无油腻感很滋润</span><span style=\"line-height:2;\">。散发的淡淡植物香气对肌肤起到了一定的舒缓作用。</span></span> \r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<span style=\"font-size:14px;line-height:1.5;\"><span style=\"line-height:2.5;\"><span style=\"line-height:2;\">重要的是，由于</span><span style=\"color:#E53333;line-height:2;\">冬季</span><span style=\"line-height:2;\">比较干燥，相比卸妆水，用</span></span><span style=\"color:#E53333;line-height:2;\">卸妆油</span><span style=\"line-height:2;\">的好处还</span><span style=\"color:#E53333;line-height:2;\">能</span><span style=\"color:#E53333;line-height:2;\">预防干燥平衡油质</span><span style=\"line-height:2;\">。</span></span> \r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<br />\r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<img src=\"http://201106.image.myqcloud.com/201106/0/de995912-2b5f-4cb3-85ad-c48d82fde607/original\" width=\"900\" height=\"718\" alt=\"\" /> \r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<br />\r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<span style=\"font-size:14px;line-height:2;\">这款洁肤油以橄榄精华油为主要成分，多种天然植物萃取。洗干净后的肌肤不紧绷，相反感觉毛孔都被打开，很清爽。</span> \r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<span style=\"font-size:14px;line-height:1.5;\"><span style=\"line-height:2;\">由于是</span><span style=\"font-size:14px;line-height:21px;white-space:normal;\"><span style=\"line-height:2;\">“天然油”独具的温和性，所以过程中</span><span style=\"color:#E53333;line-height:2;\">肌肤始终无刺激感</span><span style=\"line-height:2;\">。</span></span></span> \r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<span style=\"font-size:14px;line-height:1.5;\"><span style=\"line-height:2;\">添加了能发挥抑炎功效的迷迭香，除了卸妆还能起到改善肌肤的作用，</span><span style=\"font-size:14px;line-height:21px;white-space:normal;\"><span style=\"line-height:2.5;\"><span style=\"line-height:2;\">适用于各类肌肤。并且类似这种</span><span style=\"color:#E53333;line-height:2;\">植物萃取</span><span style=\"line-height:2;\">的</span></span><span style=\"font-size:14px;line-height:2;white-space:normal;\">配方都温和天然，</span><span style=\"line-height:2;\">所以</span><span style=\"line-height:2;\">即使是</span></span></span><span style=\"font-size:14.44444465637207px;line-height:2.5;\"><span style=\"color:#E53333;line-height:2;\">敏感肌肤也能安心使用</span><span style=\"line-height:2;\">。</span></span> \r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<br />\r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-align:center;\">\r\n	<span style=\"font-size:14px;color:#E53333;\"><strong>—————乳化实验—————</strong></span> \r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<br />\r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<span class=\"apple-converted-space\" style=\"font-size:14px;line-height:1.5;color:#000000;\">&nbsp;乳化效果是考验卸妆油</span><span style=\"font-size:14px;line-height:1.5;color:#000000;\">最重要步骤之一，所以检验是不是好产品，还是要靠证据说话，实验做起！！！</span><span></span> \r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<br />\r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<img src=\"http://201106.image.myqcloud.com/201106/0/7fa4b9b6-379c-420f-8dff-e0fe09132ae9/original\" width=\"900\" height=\"1307\" alt=\"\" /> \r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<br />\r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<span style=\"color:#000000;font-size:14px;line-height:2;\">准备一杯清水，将卸妆油倒入装有清水的杯子中，判断它的乳化速度和程度，如果有浮油就说明这个卸妆油不能完全乳化，时间久了易长粉刺</span><span style=\"color:#000000;font-size:14px;line-height:2;\">和痘痘，相反，水面若无油的话则是较为优质的卸妆油，可以放心使用。</span><span></span> \r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<br />\r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<img src=\"http://201106.image.myqcloud.com/201106/0/173f4a42-e9b5-4c7f-a17d-b61ae271894d/original\" width=\"900\" height=\"1172\" alt=\"\" /> \r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<br />\r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<span style=\"font-size:14px;color:#000000;\">滴入【薇瑟芬幻采净透洁肤油】后，水面呈现出了蛋清的样子。遇水后即分层，乳化速度较好，略有部分未完全乳化油脂漂浮水面。</span> \r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<br />\r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<img src=\"http://201106.image.myqcloud.com/201106/0/23713f96-3dae-48aa-96e5-7fb7532b4c7f/original\" width=\"900\" height=\"1090\" alt=\"\" /> \r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<br />\r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<span style=\"color:#000000;font-size:14px;line-height:2;\">经过摇晃之后充分溶解，没有挂壁现象，这样就说明水洗后无残留很清爽。</span> <span></span> \r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<br />\r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<img src=\"http://201106.image.myqcloud.com/201106/0/544bcfff-5c61-4cd1-98eb-b0a2cd436047/original\" width=\"900\" height=\"1600\" alt=\"\" /> \r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<span style=\"font-size:14px;line-height:1.5;\"><br />\r\n</span> \r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<span style=\"font-size:14px;line-height:1.5;\"><span style=\"line-height:2;\">总体来说，这款【薇瑟芬幻采净透洁肤油】非常好用！</span><strong><span style=\"line-height:2;\">值得购买！</span></strong><span style=\"font-size:14px;line-height:2;white-space:normal;\"><strong>值得购买！</strong></span><span style=\"font-size:14px;line-height:2;white-space:normal;\"><strong>值得购买！</strong></span><strong></strong><strong></strong></span> \r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<br />\r\n</p>\r\n<p class=\"MsoNormal\" style=\"margin-left:0cm;white-space:normal;\">\r\n	<b><span style=\"color:#E53333;\">》》》</span></b> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"margin-left:0cm;white-space:normal;\">\r\n	<span style=\"color:#E53333;\">&nbsp;</span><b style=\"font-size:12px;line-height:1.5;\"><span style=\"color:#E53333;\">如需代理或购买，喵主可前往</span></b><b style=\"font-size:12px;line-height:1.5;\"><span style=\"color:#E53333;\">V</span></b><b style=\"font-size:12px;line-height:1.5;\"><span style=\"color:#E53333;\">猫商场【美妆专区】</span></b> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"margin-left:0cm;white-space:normal;\">\r\n	<span style=\"font-size:12px;line-height:1.5;\"></span> \r\n</p>\r\n<p>\r\n	<br />\r\n</p>', '', '', '1');
