INSERT INTO `cms_article_data` VALUES ('95816fbe020d4d8584b35b1835e88751', '<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<span style=\"font-size:14px;font-family:&quot;\">亲爱的V猫店主：</span>\r\n</p>\r\n<p>\r\n	<span style=\"font-size:14px;font-family:&quot;\">V猫平台为帮助所有店主促进销量，招募属于自己的代理团队，日前正式上线全新的代理系统，为白金店主或特约店主同时提供支持。</span>\r\n</p>\r\n<p>\r\n	<span style=\"font-size:14px;font-family:&quot;\">我们提供的服务包括：</span>\r\n</p>\r\n<p>\r\n	<span style=\"font-size:14px;font-family:&quot;\"><br />\r\n</span>\r\n</p>\r\n<p>\r\n	<span style=\"font-size:14px;font-family:&quot;\">1、招商文案+图片/视频素材；</span>\r\n</p>\r\n<p>\r\n	<span style=\"font-size:14px;font-family:&quot;\">2、实时更新优惠代理政策；</span>\r\n</p>\r\n<p>\r\n	<span style=\"font-size:14px;font-family:&quot;\">3、V猫导师在线培训课程；</span>\r\n</p>\r\n<p>\r\n	<span style=\"font-size:14px;font-family:&quot;\"><br />\r\n</span>\r\n</p>\r\n<p>\r\n	<span style=\"font-size:14px;font-family:&quot;\">更多详情，请在微信店主群中了解，感谢您的支持，谢谢！</span>\r\n</p>\r\n<p>\r\n	<br />\r\n</p>', '', '', '1');
