INSERT INTO `cms_article_data` VALUES ('96cf80d2946b43d5bf01076efa22c4a9', '<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<span style=\"font-family:&quot;font-size:14px;\">亲爱的V猫店主：</span>\r\n</p>\r\n<p>\r\n	<span style=\"font-family:&quot;font-size:14px;color:#E53333;\"><strong>【蒲江猕猴桃家庭实惠装</strong></span><span style=\"font-family:&quot;font-size:14px;\"><span style=\"color:#E53333;\"><strong>3人团】</strong></span>上架了。原价78元团购仅需49.8元！</span>\r\n</p>\r\n<p>\r\n	<span style=\"font-family:&quot;font-size:14px;\">产品规格：30枚（单果60~70g）</span>\r\n</p>\r\n<p>\r\n	<span style=\"font-family:&quot;font-size:14px;\">白金店主代理利润：18元</span>\r\n</p>\r\n<p>\r\n	<span style=\"font-family:&quot;font-size:14px;\">特约店主代理利润：14元</span>\r\n</p>\r\n<p>\r\n	<span style=\"font-family:&quot;font-size:14px;\">马上开团吧！</span>\r\n</p>\r\n<p>\r\n	<span style=\"font-family:&quot;font-size:14px;\">温馨提示：</span><span style=\"font-family:&quot;font-size:14px;\">家庭实惠装的果子个头较小，</span><span style=\"font-family:&quot;font-size:14px;\">无异形果和花斑果，口感佳！</span><span style=\"font-family:&quot;font-size:14px;\">由于天气炎热，气温过高，加上猕猴桃本身就是后熟类水果，水份流失速度比其他果子快，所以收到的果子总重量可能存在100g~200g的误差，请详悉。</span>\r\n</p>', '', '', '1');
INSERT INTO `cms_article_data` VALUES ('972d3b9b7998448aac51be6d7f68fcd3', '<p class=\"MsoNormal\">\r\n	<br />\r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<span style=\"font-size:14px;line-height:2;\">红枣，又名大枣。特点是维生素含量非常高，有</span><span style=\"font-size:14px;line-height:2;\">“</span><span style=\"font-size:14px;line-height:2;\">天然维生素</span><span style=\"font-size:14px;line-height:2;\">丸</span><span style=\"font-size:14px;line-height:2;\">”</span><span style=\"font-size:14px;line-height:2;\">的美誉；</span> \r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<span style=\"font-size:14px;line-height:2;\">具有滋阴补阳，补血的功效，尤其是每当到了冬季；</span> \r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<span style=\"font-size:14px;line-height:2;\">天气变得寒冷的时候，我会经常买大枣放在家里，用来炖汤、熬粥、泡水喝，或者是直接当成小零食；</span> \r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<span style=\"font-size:14px;line-height:2;\">大枣的好处就不赘述了，特别是对于女生而言，所以在我们家一年四季都能看到大枣的身影；</span> \r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<span style=\"font-size:14px;line-height:2;\">不过大枣也分很多不同的产地，我最常买的是【波斯贡枣】这个品牌的大枣，他们的大枣产地是在新疆阿克苏。</span> \r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<span style=\"font-size:14px;line-height:2;\"><br />\r\n</span> \r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<span></span> \r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<img src=\"http://201106.image.myqcloud.com/201106/0/81bf5a5d-9943-4d3d-8f07-fa7d72ce3773/original\" width=\"900\" height=\"562\" alt=\"\" /> \r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<br />\r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<span style=\"font-size:14px;line-height:2;\">这次一共买了三大袋，两袋自己吃的，还有一袋拿来送人；</span> \r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<span style=\"font-size:14px;line-height:2;\">外包装的设计，都采用的是比较鲜艳的色彩， 和大枣的红色相得益彰；</span> \r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<span style=\"font-size:14px;line-height:2;\">简洁大气的包装，用来当作礼品送人都是不错的选择；</span> \r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<span style=\"font-size:14px;line-height:2;\"><br />\r\n</span> \r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<span style=\"font-size:14px;line-height:2;\"><img src=\"http://201106.image.myqcloud.com/201106/0/98520f17-8a8a-44e9-af27-cd0fa574cb65/original\" width=\"900\" height=\"706\" alt=\"\" /><br />\r\n</span> \r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<span style=\"font-size:14px;line-height:2;\"><br />\r\n</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-align:center;\">\r\n	<span style=\"font-size:14px;line-height:2;\">【<strong>波斯贡枣</strong>】</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-align:center;\">\r\n	————<span style=\"color:#E53333;\"><strong style=\"text-align:center;white-space:normal;\"><span style=\"background-color:#E53333;color:#FFFFFF;\">六星级</span></strong><span style=\"text-align:center;white-space:normal;\"></span></span><span style=\"font-size:12px;line-height:1.5;\">————</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-align:center;\">\r\n	<br />\r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<img src=\"http://201106.image.myqcloud.com/201106/0/11940042-15bb-4e0c-be51-0fa7fcee7e1f/original\" width=\"900\" height=\"1131\" alt=\"\" /> \r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<img src=\"http://201106.image.myqcloud.com/201106/0/4e43d012-950e-48f3-bfdb-1412a0921e99/original\" width=\"790\" height=\"279\" alt=\"\" /> \r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<br />\r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<span style=\"line-height:2;font-size:14px;\">虽然是同一产地，但是【波斯贡】的大枣还是精确的分了星级；</span> \r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<span style=\"line-height:2;font-size:14px;\">枣的星级是厂家根据每家农户枣的质量，经过层层筛选出来的，然后再进行包装的确定；</span> \r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<span style=\"line-height:2;font-size:14px;\">这一款六星的【波斯贡枣】大礼包是拿来送给朋友的；</span> \r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<span style=\"line-height:2;font-size:14px;\">因为包装上档次，分量也比较多；</span> \r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<span style=\"line-height:2;font-size:14px;\">而且大枣性味甘温，能补中益气、养血安神，也算是给朋友送去一份健康；</span> \r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<br />\r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<img src=\"http://201106.image.myqcloud.com/201106/0/f30b9981-d823-4988-a0ee-67e997a51ae3/original\" width=\"900\" height=\"506\" alt=\"\" /> \r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<br />\r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<span style=\"line-height:2;font-size:14px;\">并且里面的每颗枣都是单独包装，由于是送人的就没有打开包装了；</span> \r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<span style=\"line-height:2;font-size:14px;\">之前自己买来吃的时候，也是6星级，只不过是小包装的；</span> \r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<span style=\"line-height:2;font-size:14px;\">这个星级枣的个头比较大，测量了一下高度平均是在5厘米左右，色泽鲜亮；</span> \r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<span style=\"line-height:2;font-size:14px;\"><br />\r\n</span> \r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<span style=\"line-height:2;font-size:14px;\"><img src=\"http://201106.image.myqcloud.com/201106/0/b4fc08ef-f45a-42eb-a028-768183de605d/original\" width=\"376\" height=\"252\" alt=\"\" /><br />\r\n</span> \r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<span style=\"line-height:2;font-size:14px;\"><br />\r\n</span> \r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<span style=\"font-size:14px;line-height:2;\">由于新疆的独特自然环境，昼夜温差大、至少15小时的阳光照射和沙质土壤培养，雪水滋养后；</span> \r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<span style=\"font-size:14px;line-height:2;\">新疆阿克苏的大枣总体上来说都具有皮薄，肉质很扎实，细密，味道自然香甜的特点；</span> \r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<span style=\"font-size:14px;line-height:2;\">而且枣核很小，这对于爱枣的人来说，枣核小就意味着枣肉多，因此同等价位下枣核小的性价比更高；</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-align:center;\">\r\n	<span style=\"white-space:normal;\"><br />\r\n</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-align:center;\">\r\n	<span style=\"white-space:normal;\">【<strong>波斯贡枣</strong>】</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-align:center;\">\r\n	————<span style=\"background-color:#E53333;color:#FFFFFF;\"><strong>钻石级</strong></span>————\r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<br />\r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<img src=\"http://201106.image.myqcloud.com/201106/0/ff17c09d-c8d3-4b56-8fd5-cccd7920ba19/original\" width=\"900\" height=\"1335\" alt=\"\" /> \r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<br />\r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<span style=\"line-height:2;font-size:14px;\">这款是【波斯贡枣】里面级别最高的钻石级别；</span> \r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<span style=\"line-height:2;font-size:14px;\">光听钻石级别就能想象这个枣的分量了；</span> \r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<br />\r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<img src=\"http://201106.image.myqcloud.com/201106/0/9adcff2f-6105-4960-ad6e-2eddb8acb371/original\" width=\"900\" height=\"624\" alt=\"\" /> \r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<br />\r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<span style=\"line-height:2;font-size:14px;\">事实上，它确实也对得起钻石级别的称号，生产日期新鲜，外包装精美，细致，袋子上还专门设计了密封条，提高了大枣的保存期限；</span> \r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<span style=\"line-height:2;font-size:14px;\">说了外在，钻石级别的枣内在也非常有料，</span><span style=\"line-height:2;font-size:14px;\">早前听说过，阿克苏实验林场被誉为</span><span style=\"line-height:2;font-size:14px;\">“</span><span style=\"line-height:2;font-size:14px;\">中国枣园中的枣园</span><span style=\"line-height:2;font-size:14px;\">”；</span> \r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<span style=\"line-height:2;font-size:14px;\">我想钻石级别的枣应该算得上是园子里的标杆性产品了吧；</span> \r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<span style=\"line-height:2;font-size:14px;\"><br />\r\n</span> \r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<span style=\"line-height:2;font-size:14px;\"><img src=\"http://201106.image.myqcloud.com/201106/0/45d345bb-cacc-4225-816a-2a156ac698cf/original\" width=\"277\" height=\"323\" alt=\"\" /><br />\r\n</span> \r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<br />\r\n</p>\r\n<p class=\"MsoNormal\">\r\n	从口感上来说，钻石级别的大枣同样具备皮薄、肉厚、质地密集的特点；\r\n</p>\r\n<p class=\"MsoNormal\">\r\n	但是相比起来，个人觉得这个枣的甜度和松软度更甚一筹；\r\n</p>\r\n<p class=\"MsoNormal\">\r\n	而且干枣的含糖量竟然高达<span>70%</span>，远比甜菜和甘蔗这两种制糖原料的含糖量高得多；\r\n</p>\r\n<p class=\"MsoNormal\">\r\n	所以民间有“一天三枣，一生不老”的说法；\r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-align:center;\">\r\n	<span><br />\r\n</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-align:center;\">\r\n	<span style=\"font-size:12px;line-height:1.5;\">&nbsp;</span><span style=\"font-size:12px;line-height:1.5;\">【</span><span style=\"font-size:12px;line-height:1.5;\"><span style=\"font-size:14px;\"><strong>南疆红玉</strong></span>】</span><span style=\"font-size:12px;line-height:1.5;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-align:center;\">\r\n	————<span style=\"background-color:#E53333;color:#FFFFFF;\"><strong>四星级</strong></span>————\r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-align:center;\">\r\n	<br />\r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<img src=\"http://201106.image.myqcloud.com/201106/0/db863a24-42f6-4791-8db9-b70491f21201/original\" width=\"900\" height=\"1284\" alt=\"\" /> \r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<br />\r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<span style=\"font-size:14px;line-height:2;\">虽然这个不是【波斯贡枣】系列，其实和【波斯贡枣】同一个产地：新疆生产建设兵团阿拉尔市；</span> \r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<span style=\"font-size:14px;line-height:2;\">并且这款大枣属于四星级的枣；</span> \r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<span style=\"font-size:14px;line-height:2;\">四星级的枣相交于其他星级的枣，虽然个头小一些，但口感上并没有太大的差别；</span> \r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<br />\r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<img src=\"http://201106.image.myqcloud.com/201106/0/a81d78b9-74e6-482f-bb77-702e1152a9b2/original\" width=\"960\" height=\"540\" alt=\"\" /> \r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<br />\r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<span style=\"font-size:14px;\">打开包装一共三小袋，枣的色泽鲜亮；</span> \r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<span style=\"font-size:14px;\">个人认为区别商超里面的售卖的大枣，这款枣的性价比更高；</span> \r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<span style=\"font-size:14px;\">因为个头比较小一些，所以我就直接放在办公室当小零食吃啦！（</span><span style=\"font-size:14px;\">ps</span><span style=\"font-size:14px;\">：大枣出厂之前就经过了好几次的反复冲洗，所以比较放心直接食用了。）</span> \r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<br />\r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<img src=\"http://201106.image.myqcloud.com/201106/0/a0f2442d-7d82-4a7e-9552-fb7d102064cd/original\" width=\"960\" height=\"639\" alt=\"\" /> \r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<br />\r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<span style=\"line-height:2;font-size:14px;\">大枣被誉为</span><span style=\"line-height:2;font-size:14px;\">“</span><span style=\"line-height:2;font-size:14px;\">百果之王</span><span style=\"line-height:2;font-size:14px;\">”</span><span style=\"line-height:2;font-size:14px;\">，也是一味中药材，保健功效特别强；</span> \r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<span style=\"line-height:2;font-size:14px;\">有研究表明，大枣具有抗衰老和预防癌症的功效；</span> \r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<span style=\"line-height:2;font-size:14px;\">大枣对于女性来说，既补气养血又美容养颜，祛斑、延缓衰老等等，是每一位爱美女性都应该必备的食物，也是老年人的养生最佳品；</span> \r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<span style=\"line-height:2;font-size:14px;\">就食补而言，一般我会用红枣来炖鸡汤和煮银耳，简直是冬季滋补养生佳品；</span> \r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<br />\r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<img src=\"http://201106.image.myqcloud.com/201106/0/b5f394ed-a741-4150-9f45-97349bea4b7f/original\" width=\"960\" height=\"683\" alt=\"\" /> \r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<br />\r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-align:center;\">\r\n	————<span style=\"background-color:#E53333;color:#FFFFFF;font-size:14px;\"><strong>大枣鉴别方法</strong></span>————\r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<br />\r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<span style=\"background-color:#FFFFFF;color:#333333;font-family:微软雅黑;font-size:14px;line-height:22px;white-space:pre-wrap;\">首先，好的大枣皮色紫红，颗粒大而均匀、果形短壮圆整，皱纹少，痕迹浅；</span><span style=\"color:#333333;font-family:微软雅黑;font-size:14px;line-height:22px;white-space:pre-wrap;\">皮薄核小，肉质厚而细实；如果皱纹多，痕迹深，果形凹瘪，则肉质差和未成熟的鲜枣制成的干品。 </span>\r\n</p>\r\n<pre style=\"margin-top:0px;margin-bottom:0px;padding:0px;white-space:pre-wrap;word-wrap:break-word;color:#333333;font-size:14px;line-height:22px;background-color:#FFFFFF;font-family:微软雅黑;\">&nbsp;</pre>\r\n<pre style=\"margin-top:0px;margin-bottom:0px;padding:0px;white-space:pre-wrap;word-wrap:break-word;color:#333333;font-size:14px;line-height:22px;background-color:#FFFFFF;font-family:微软雅黑;\">如果红枣的蒂端有穿孔或粘有咖啡色或深褐色的粉末，这说明红枣已被虫蛀了，掰开红枣可看到肉核之间有虫屎。吃时要将虫屎、烂枣等剔除干净。 \r\n\r\n</pre>\r\n<pre style=\"margin-top:0px;margin-bottom:0px;padding:0px;white-space:pre-wrap;word-wrap:break-word;color:#333333;font-size:14px;line-height:22px;background-color:#FFFFFF;font-family:微软雅黑;\">其次，口尝味甜，用手将红枣成把紧捏一下，如感到滑糯又不松泡，说明质细紧实，枣身干，核小；如果甜味差、有酸涩味，用手捏，松软粗糙，质量就差，要是湿软而粘手，说明枣身较潮，不耐久贮，易于霉烂变质。 </pre>\r\n<span style=\"font-size:14px;line-height:2;color:#000000;\"></span> \r\n<p>\r\n	<br />\r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-align:center;\">\r\n	<span style=\"font-size:14px;line-height:2;color:#000000;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-align:center;\">\r\n	<span style=\"font-size:14px;line-height:2;color:#000000;\"><br />\r\n</span>\r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-align:center;\">\r\n	<span style=\"font-size:14px;line-height:2;color:#000000;\">————<span style=\"background-color:#E53333;color:#FFFFFF;font-size:14px;\"><strong>红枣百合银耳汤</strong></span>————</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-align:center;\">\r\n	<span style=\"font-size:14px;line-height:2;color:#000000;\"><br />\r\n</span>\r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-align:center;\">\r\n	<br />\r\n</p>\r\n<div style=\"text-align:left;\">\r\n	<span style=\"font-size:14px;line-height:2;\">用料：</span> \r\n</div>\r\n<span style=\"font-size:14px;line-height:2;color:#000000;\"> \r\n<p>\r\n	<table style=\"box-sizing:border-box;vertical-align:top;border-spacing:0px;width:564px;margin:5px 0px;font-size:13px;line-height:20px;\">\r\n		<tbody style=\"box-sizing:border-box;vertical-align:top;\">\r\n			<tr itemprop=\"ingredients\" style=\"box-sizing:border-box;vertical-align:top;width:563px;\">\r\n				<td class=\"name has-border\" style=\"box-sizing:border-box;vertical-align:top;border:1px solid #EBE9E7;padding:5.6px 5.6px 5.6px 20px;width:280px;font-weight:bo