INSERT INTO `cms_article_data` VALUES ('a2fdafdde21a49cea763a3c56e58ccc2', '<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"color:#FF0000;font-family:微软雅黑;font-size:10.5pt;font-weight:bold;line-height:1.5;text-indent:0pt;\"> </span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;white-space:normal;\">\r\n	<span style=\"font-family:微软雅黑;color:#222222;font-size:10.5pt;line-height:1.5;\">本期体验官体验产品</span><span style=\"font-family:微软雅黑;color:#FF0000;font-weight:bold;font-size:10.5pt;line-height:1.5;\">【</span><span style=\"font-family:微软雅黑;color:#FF0000;font-weight:bold;font-size:10.5pt;line-height:1.5;\">韩黛M美逆时空嫩红精华】</span><span style=\"font-family:微软雅黑;color:#FF0000;font-weight:bold;font-size:10.5pt;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;white-space:normal;\">\r\n	<span style=\"font-family:微软雅黑;color:#FF0000;font-weight:bold;font-size:14pt;line-height:1.5;\">市场价：128元</span><span style=\"font-family:\'sans serif\';font-size:12pt;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;white-space:normal;\">\r\n	<span style=\"font-family:微软雅黑;color:#FF0000;font-weight:bold;font-size:14pt;line-height:1.5;\">体验价：0元</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"color:#FF0000;font-family:微软雅黑;font-size:10.5pt;font-weight:bold;line-height:1.5;text-indent:0pt;\"> </span> \r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<span style=\"font-family:微软雅黑;font-size:10.5pt;\"><span style=\"color:#000000;\"><strong>推荐指数：★</strong></span><span style=\"color:#FF0000;font-family:微软雅黑;font-size:14px;line-height:21px;white-space:normal;\"><span style=\"color:#000000;\"><strong>★</strong></span><span style=\"color:#FF0000;font-family:微软雅黑;font-size:14px;line-height:21px;white-space:normal;\"><span style=\"color:#000000;\"><strong>★</strong></span><span style=\"font-family:微软雅黑;font-size:14px;line-height:21px;white-space:normal;color:#000000;\"><strong>★</strong></span></span></span></span> \r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<span style=\"font-family:微软雅黑;font-size:10.5pt;\"><span style=\"color:#FF0000;font-family:微软雅黑;font-size:14px;line-height:21px;white-space:normal;\"><span style=\"color:#FF0000;font-family:微软雅黑;font-size:14px;line-height:21px;white-space:normal;\"><span style=\"color:#FF0000;font-family:微软雅黑;font-size:14px;line-height:21px;white-space:normal;\"><span style=\"color:#000000;\"><strong>推荐理由：</strong></span><span style=\"color:#000000;\"></span></span><span style=\"color:#000000;\"></span></span><span style=\"color:#000000;\"></span></span><span style=\"color:#000000;\"></span><span style=\"color:#000000;\"><strong>专注改善肌肤暗沉、有效淡化黑色素，期待你的肌肤嫩红如初，散发迷人光彩！</strong></span></span> \r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<span style=\"font-family:微软雅黑;font-size:10.5pt;color:#000000;\"><strong>报名方式：点击首页“V猫体验官”查看详细后，进入“我要报名”。</strong></span>\r\n</p>\r\n<p>\r\n	<span style=\"font-family:微软雅黑;font-size:14px;line-height:21px;color:#000000;\"><strong>报名时间：11月5日17:30报名准时开抢</strong></span> \r\n</p>\r\n<p>\r\n	<span style=\"font-family:微软雅黑;\"><span style=\"font-size:18px;line-height:21px;color:#E53333;\"><strong>体验官名额仅限50个！</strong></span></span> \r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"color:#FF0000;font-family:微软雅黑;font-size:10.5pt;font-weight:bold;line-height:1.5;text-indent:0pt;\">你的肯定，是产品入驻的唯一准则！</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-family:微软雅黑;font-size:10.5pt;line-height:1.5;\">我们不迷信专业人士絮絮叨叨的产品点评，不迷信提供的冗长数据分析，我们只相信身为自己人的你！</span><span style=\"font-family:微软雅黑;font-size:10.5pt;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-family:微软雅黑;font-size:10.5pt;line-height:1.5;\">你反馈的体验官报告决定了产品在V猫平台的去留！好产品我们留下，并竭尽全力的帮助你让它成为你小店中的热销产品，让开店更有价值！</span><span style=\"font-family:微软雅黑;font-size:10.5pt;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-family:微软雅黑;font-size:10.5pt;line-height:1.5;\">V猫小店真挚的邀请你成为我们的V猫体验官！将真正好的产品推荐给真正需要它的人！</span> \r\n</p>\r\n<img src=\"http://201106.image.myqcloud.com/201106/0/73829e58-2933-448d-8677-209be4568530/original\" width=\"660\" height=\"8713\" alt=\"\" /><span style=\"line-height:1.5;\"></span><br />\r\n<p>\r\n	<br />\r\n</p>', '', '', '1');
INSERT INTO `cms_article_data` VALUES ('a33fde0ebc064995ad108bf3e7791468', '<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<span style=\"font-family:微软雅黑;color:#222222;font-size:10.5pt;\">细心的V猫店主们一定会发现，直接用手机号注册的用户头像是灰不溜秋的，而且名字都是默认的<span style=\"font-size:16px;color:#E53333;\"><strong>邀请码</strong></span>，就算用微博、微信、QQ登陆，用户名也是邀请码。都说人靠衣装，开门做生意赢得好影响的第一步需要好好包装包装自己。</span><span style=\"font-family:微软雅黑;color:#222222;font-size:10.5pt;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<span style=\"font-family:微软雅黑;color:#222222;font-size:10.5pt;\">设置流程如下：</span> \r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<span style=\"font-family:微软雅黑;color:#222222;font-size:10.5pt;\"><br />\r\n</span> \r\n</p>\r\n<p>\r\n	<img src=\"http://201106.image.myqcloud.com/201106/0/f62fe250-1428-479a-99eb-c1973ff70b7e/original\" width=\"800\" height=\"473\" alt=\"\" /> \r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<span style=\"font-family:微软雅黑;color:#222222;font-size:10.5pt;\">拥有一个具有识别性的名字非常重要。在朋友圈分享的链接里都会带有你设置的小店名称。过长的名字不容易显示完整，具有个人标记的名字是一个好的选择。（比如和微博、微信、QQ的名称相同）</span><span style=\"font-family:微软雅黑;color:#222222;font-size:10.5pt;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<span style=\"font-family:微软雅黑;color:#222222;font-size:10.5pt;\"><br />\r\n</span> \r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<span style=\"font-family:微软雅黑;color:#222222;font-size:10.5pt;\">信任是交易的基础，设置一个更为清爽的头像能让朋友们通过链接进入小店时，再次确认店主身份（头像最好是本人照片）。</span><span style=\"font-family:微软雅黑;color:#222222;font-size:10.5pt;\"></span> \r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<img src=\"http://201106.image.myqcloud.com/201106/0/da9c6e83-7941-447f-9352-1282920f14e1/original\" width=\"450\" height=\"800\" alt=\"\" /> \r\n</p>\r\n<p>\r\n	<br />\r\n</p>', '', '', '1');
INSERT INTO `cms_article_data` VALUES ('a3914a92da4f4079a5a63eb044b32f5b', '<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<span style=\"font-family:&quot;font-size:14px;\">亲爱的V猫店主：</span>\r\n</p>\r\n<p>\r\n	<span style=\"font-family:&quot;font-size:14px;\"><strong><span style=\"color:#E53333;\">【V猫庄园直播团】</span></strong>已于今日早晨6点出发前往攀枝花凯特芒庄园，<strong><span style=\"color:#E53333;\">下午3点整</span></strong>将开启第三波凯特芒现摘现发的的直播活动。届时直播团将通过【映客直播】在现场帮助店主们招募团队成员，望店主们踊跃分享链接。招募活动结束后，客服人员将统计新晋店主名单，并在V猫社群中公示，若您有客户在此名单中请联系V猫总管（微信号：vmao222），我们会将其绑定到您的团队。</span>\r\n</p>\r\n<p>\r\n	<span style=\"font-family:&quot;font-size:14px;\">感谢您的支持，谢谢！</span>\r\n</p>\r\n<p>\r\n	<span style=\"font-family:&quot;font-size:14px;\"></span>\r\n</p>', '', '', '1');
