INSERT INTO `cms_article_data` VALUES ('b73dc22e4df2476299d03a08e186b1d6', '<p class=\"MsoNormal\" style=\"line-height:200%;\">\r\n	<b style=\"line-height:200%;\"><span style=\"font-size:14px;line-height:1.5;\"> </span></b>\r\n</p>\r\n<p class=\"MsoNormal\" style=\"margin-left:0cm;\">\r\n	<b style=\"line-height:200%;\"><b><span style=\"font-size:10.5pt;font-family:宋体;color:#E53333;\">什么是</span></b><b><span style=\"font-size:10.5pt;color:#E53333;\">V</span></b><b><span style=\"font-size:10.5pt;font-family:宋体;color:#E53333;\">猫体验官？</span></b><span style=\"font-size:9pt;\"></span> </b>\r\n</p>\r\n<p class=\"MsoNormal\" style=\"margin-left:0cm;\">\r\n	<b style=\"line-height:200%;\"><span style=\"font-size:10.5pt;\">V</span><span style=\"font-size:10.5pt;font-family:宋体;\">猫体验官是平台根据供应商商品入驻招募的一批店主，他们能</span><span style=\"font-size:10.5pt;font-family:宋体;color:#E53333;\">优先体验待上架产品</span><span style=\"font-size:10.5pt;font-family:宋体;\">，主要以</span><span style=\"font-size:10.5pt;font-family:宋体;\">具有社交电商基因的工业化农副特产类产品为主</span><span style=\"font-size:10.5pt;font-family:宋体;\">。并在规定日期之内提交体验报告，报告数据将决定此类通过</span><span style=\"font-size:10.5pt;\">V</span><span style=\"font-size:10.5pt;font-family:宋体;\">猫平台初审的产品能否最终上架销售。</span><span style=\"font-size:10.5pt;\"></span> </b>\r\n</p>\r\n<p class=\"MsoNormal\" style=\"margin-left:0cm;\">\r\n	<b style=\"line-height:200%;\"><br />\r\n</b>\r\n</p>\r\n<p class=\"MsoNormal\" style=\"margin-left:0cm;\">\r\n	<b style=\"line-height:200%;\"><b><span style=\"font-size:10.5pt;font-family:宋体;color:#E53333;\">为什么设置</span></b><b><span style=\"font-size:10.5pt;color:#E53333;\">V</span></b><b><span style=\"font-size:10.5pt;font-family:宋体;color:#E53333;\">猫体验官？</span></b><b><span style=\"font-size:10.5pt;\"></span></b> </b>\r\n</p>\r\n<p class=\"MsoNormal\" style=\"margin-left:0cm;\">\r\n	<b style=\"line-height:200%;\"><span style=\"font-size:10.5pt;font-family:宋体;\">在</span><span style=\"font-size:10.5pt;\">V</span><span style=\"font-size:10.5pt;font-family:宋体;\">猫小店看来，对于产品的审核，专家点评！权威机构！第三者的视角永远不如身为自己人的店主们，肩负着</span><span style=\"font-size:10.5pt;\">V</span><span style=\"font-size:10.5pt;font-family:宋体;\">猫小店产品质量审核的重大责任，你们对产品的体验感才是产品上架的最好证明。</span><span style=\"font-size:10.5pt;font-family:宋体;\"> </span><span style=\"font-size:10.5pt;\"></span> </b>\r\n</p>\r\n<p class=\"MsoNormal\" style=\"margin-left:0cm;\">\r\n	<b style=\"line-height:200%;\"><br />\r\n</b>\r\n</p>\r\n<p class=\"MsoNormal\" style=\"margin-left:0cm;\">\r\n	<b style=\"line-height:200%;\"><b><span style=\"font-size:10.5pt;color:#E53333;\">V</span></b><b><span style=\"font-size:10.5pt;font-family:宋体;color:#E53333;\">猫体验官的重要性？</span></b><span style=\"font-size:9pt;\"></span> </b>\r\n</p>\r\n<p class=\"MsoNormal\" style=\"margin-left:0cm;\">\r\n	<b style=\"line-height:200%;\"><span style=\"font-size:10.5pt;\">1</span><span style=\"font-size:10.5pt;font-family:宋体;\">、对</span><span style=\"font-size:10.5pt;font-family:宋体;color:#E53333;\">供应商</span><span style=\"font-size:10.5pt;font-family:宋体;\">而言，凡是进驻</span><span style=\"font-size:10.5pt;\">V</span><span style=\"font-size:10.5pt;font-family:宋体;\">猫小店的产品必须先接受体验官的体验，因此</span><span style=\"font-size:10.5pt;\">V</span><span style=\"font-size:10.5pt;font-family:宋体;\">猫体验官反馈的报告直接决定了产品在</span><span style=\"font-size:10.5pt;\">V</span><span style=\"font-size:10.5pt;font-family:宋体;\">猫平台的去留！</span><span style=\"font-size:9pt;\"></span> </b>\r\n</p>\r\n<p class=\"MsoNormal\" style=\"margin-left:0cm;\">\r\n	<b style=\"line-height:200%;\"><span style=\"font-size:10.5pt;\">2</span><span style=\"font-size:10.5pt;font-family:宋体;\">、作为拥有</span><span style=\"font-size:10.5pt;\">V</span><span style=\"font-size:10.5pt;font-family:宋体;\">猫体验官身份的</span><span style=\"font-size:10.5pt;font-family:宋体;color:#E53333;\">店主</span><span style=\"font-size:10.5pt;font-family:宋体;\">，能最直观的感受产品效果，深度的了解产品属性，积累更多专业知识后再向朋友推荐，用专业态度，优质产品来提高朋友圈营销信任度。</span><span style=\"font-size:9pt;\"></span> </b>\r\n</p>\r\n<p class=\"MsoNormal\" style=\"margin-left:0cm;\">\r\n	<b style=\"line-height:200%;\"><span style=\"font-size:10.5pt;\">3</span><span style=\"font-size:10.5pt;font-family:宋体;\">、对</span><span style=\"font-size:10.5pt;font-family:宋体;color:#E53333;\">社交圈的好友</span><span style=\"font-size:10.5pt;font-family:宋体;\">而言，把真正优质的产品推荐给好友是对他们的负责，良好的客户体验需要</span><span style=\"font-size:10.5pt;\">V</span><span style=\"font-size:10.5pt;font-family:宋体;\">猫体验官和</span><span style=\"font-size:10.5pt;\">V</span><span style=\"font-size:10.5pt;font-family:宋体;\">猫小店共同打造的！</span><span style=\"font-size:10.5pt;\"></span> </b>\r\n</p>\r\n<p class=\"MsoNormal\" style=\"margin-left:0cm;\">\r\n	<b style=\"line-height:200%;\"><span style=\"font-size:10.5pt;\">4</span><span style=\"font-size:10.5pt;font-family:宋体;\">、对</span><span style=\"font-size:10.5pt;font-family:宋体;color:red;\">团队成员</span><span style=\"font-size:10.5pt;font-family:宋体;\">而言，你获得体验产品后发放部分给感兴趣的朋友，邀请他们共同检验产品质量，树立销售该产品的信心。</span><span style=\"font-size:10.5pt;\"></span> </b>\r\n</p>\r\n<p class=\"MsoNormal\" style=\"margin-left:0cm;\">\r\n	<b style=\"line-height:200%;\"><span style=\"font-size:9pt;\">&nbsp;</span> </b>\r\n</p>\r\n<p class=\"MsoNormal\" style=\"margin-left:0cm;\">\r\n	<b style=\"line-height:200%;\"><b><span style=\"font-size:10.5pt;color:#E53333;\">V</span></b><b><span style=\"font-size:10.5pt;font-family:宋体;color:#E53333;\">猫体验官淘汰机制？</span></b><span style=\"font-size:9pt;\"></span> </b>\r\n</p>\r\n<p class=\"MsoNormal\" style=\"margin-left:0cm;\">\r\n	<b style=\"line-height:200%;\"><span style=\"font-size:10.5pt;\">V</span><span style=\"font-size:10.5pt;font-family:宋体;\">猫小店会定期安排产品，招募喵主来担任</span><span style=\"font-size:10.5pt;\">V</span><span style=\"font-size:10.5pt;font-family:宋体;\">猫体验官。在体验活动截止期内，</span><span style=\"font-size:10.5pt;\">V</span><span style=\"font-size:10.5pt;font-family:宋体;\">猫体验官需要在</span><span style=\"font-size:10.5pt;\">V</span><span style=\"font-size:10.5pt;font-family:宋体;\">猫小店</span><span style=\"font-size:10.5pt;\">APP</span><span style=\"font-size:10.5pt;font-family:宋体;\">上完成并提交相应产品的体验报告。若未能在有效期内提交报告的</span><span style=\"font-size:10.5pt;\"></span> </b>\r\n</p>\r\n<p class=\"MsoNormal\" style=\"margin-left:0cm;\">\r\n	<b style=\"line-height:200%;\"><span style=\"font-size:10.5pt;font-family:宋体;\">喵主，</span><span style=\"font-size:10.5pt;font-family:宋体;color:#E53333;\">将永久的失去</span><span style=\"font-size:10.5pt;color:#E53333;\">V</span><span style=\"font-size:10.5pt;font-family:宋体;color:#E53333;\">猫体验官的申请资格。</span><span style=\"font-size:9pt;\"></span> </b>\r\n</p>\r\n<p class=\"MsoNormal\" style=\"margin-left:0cm;\">\r\n	<b style=\"line-height:200%;\"><br />\r\n</b>\r\n</p>\r\n<p class=\"MsoNormal\" style=\"margin-left:0cm;\">\r\n	<b style=\"line-height:200%;\"><b><span style=\"font-size:10.5pt;color:#E53333;\">V</span></b><b><span style=\"font-size:10.5pt;font-family:宋体;color:#E53333;\">猫体验官怎么报名？</span></b><span style=\"font-size:9pt;\"><br />\r\n</span><span style=\"font-size:10.5pt;font-family:宋体;\">在</span><span style=\"font-size:10.5pt;\">APP</span><span style=\"font-size:10.5pt;font-family:宋体;\">首页，点击</span><span style=\"font-size:10.5pt;\">“V</span><span style=\"font-size:10.5pt;font-family:宋体;\">猫体验官</span><span style=\"font-size:10.5pt;\">”</span><span style=\"font-size:10.5pt;font-family:宋体;\">按键后，点击最新公告链接，查看</span><span style=\"font-size:10.5pt;\">V</span><span style=\"font-size:10.5pt;font-family:宋体;\">猫体验官计划，在文章末尾点击</span><span style=\"font-size:10.5pt;\">“</span><span style=\"font-size:10.5pt;font-family:宋体;\">我要报名</span><span style=\"font-size:10.5pt;\">”</span><span style=\"font-size:10.5pt;font-family:宋体;\">按键。</span><span style=\"font-size:9pt;\"></span> </b>\r\n</p>\r\n<p class=\"MsoNormal\" style=\"margin-left:0cm;\">\r\n	<b style=\"line-height:200%;\"><span style=\"font-size:10.5pt;font-family:宋体;\">第一步：点击</span><span style=\"font-size:10.5pt;\">“V</span><span style=\"font-size:10.5pt;font-family:宋体;\">猫体验官</span><span style=\"font-size:10.5pt;\">”</span><span style=\"font-size:10.5pt;font-family:宋体;\">按键；</span><span style=\"font-size:9pt;\"></span> </b>\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p class=\"MsoNormal\" align=\"left\" style=\"line-height:200%;\">\r\n	<span style=\"font-size:14px;line-height:1.5;\"><img src=\"http://201106.image.myqcloud.com/201106/0/5380b6ba-b52d-4ea0-8d18-0f6cfbbc6371/original\" width=\"638\" height=\"732\" alt=\"\" /><br />\r\n</span> \r\n</p>', '', '', '1');
INSERT INTO `cms_article_data` VALUES ('b83381355ba343d6a5306cafa2ec0543', '<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<span style=\"font-family:&quot;font-size:14px;\">亲爱的V猫店主：</span>\r\n</p>\r\n<p>\r\n	<span style=\"color:#434343;font-family:&quot;font-size:14px;line-height:22px;white-space:normal;background-color:#FFFFFF;\">全球最强台风的“莫兰蒂”9月15日在福建厦门登陆后，携风裹雨肆虐闽南地区，导致多地受灾。为保证V猫小店产品的优质、物流的快捷，我们暂时停止向福建地区发货，望店主们详悉！</span>\r\n</p>\r\n<p>\r\n	<span style=\"color:#434343;font-family:&quot;font-size:14px;line-height:22px;white-space:normal;background-color:#FFFFFF;\">感谢您的支持，谢谢！</span>\r\n</p>', '', '', '1');
