INSERT INTO `cms_article_data` VALUES ('c59fdce1fc33424bb3e855d9bff4e783', '<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<span style=\"font-family:\'Microsoft YaHei\';font-size:14px;color:#64451D;\">【V猫有话说】微商是什么?</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-family:\'Microsoft YaHei\';font-size:14px;color:#64451D;\"><br />\r\n</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-family:\'Microsoft YaHei\';font-size:16px;color:#000000;\">V猫一张图告诉你！微商的潜力很大，但也瓶颈多多，深入了解一下，才能知道微商有没有出路，“朋友圈营销”是不是真正的微商，出路在哪里？</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-family:\'Microsoft YaHei\';font-size:16px;\"><br />\r\n</span> \r\n</p>\r\n<p>\r\n	<img src=\"http://201106.image.myqcloud.com/201106/0/24a8dd46-0ff0-4826-ae31-fc4cbe8553f4/original\" width=\"640\" height=\"3455\" alt=\"\" /> \r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<span style=\"font-family:\'Microsoft YaHei\';font-size:14px;color:#DFC5A4;\"> ——图源网络</span>\r\n</p>\r\n<p>\r\n	<span style=\"font-family:\'Microsoft YaHei\';font-size:14px;\"><span style=\"font-size:16px;\">微商受到的非议越来越大！出路在哪里？……</span><span style=\"font-size:16px;\">V猫从源头上，抓住优质货源，引入V猫小店平台化运营，佣金提成、团队分行、上架奖励、促销奖励；每个供应商，都是行业专家；采用战队模式，让每个小白都能秒变大咖；品牌入驻V猫，需要经过真实的用户体验来评判。不是所有微商都可以叫V猫，都是开店，但V猫小店却大不同。你，还在犹豫什么？</span></span> \r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<br />\r\n</p>', '', '', '1');
