INSERT INTO `cms_article_data` VALUES ('c8030aac214a4da0a1dfe471ed874d65', '<p class=\"MsoNormal\">\r\n	<br />\r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-align:justify;\">\r\n	<span style=\"line-height:1.5;font-size:14px;\"> </span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-align:justify;text-justify:inter-ideograph;line-height:11.0pt;\">\r\n	<span style=\"font-size:14px;line-height:1.5;\">高尔基说过：时间是最公平合理的，它从不多给谁一分。所以每个人，无论是贫贱、富贵，都有这样珍贵的财富——时间。勤劳者能叫时间留下串串果实，懒惰者只能让时间留给他们一头白发，两手空空。因此做人应当这样，做事亦是如此。经过时间历练和沉淀后的何鲜卤正是这样，无论是从品牌的定位还是产品细节上，都给人以醇正的品质感。</span><o:p></o:p>\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-align:justify;\">\r\n	<img src=\"http://201106.image.myqcloud.com/201106/0/7b4f3481-2a21-4b56-af37-06fb9f9ee039/original\" width=\"900\" height=\"1550\" alt=\"\" /> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-align:justify;\">\r\n	<br />\r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-align:justify;text-justify:inter-ideograph;line-height:11.0pt;\">\r\n	<span style=\"font-size:14px;line-height:1.5;\">何鲜卤采用的真空包装，拆封后还进行了第二次包装，不仅保鲜还利于携带。细节之处的设计尽显【何鲜卤】品牌应有的匠人精神。而包装袋上张贴着的【何鲜卤】品牌故事，充分展示了【何鲜卤】对于手工卤制的态度。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-align:justify;text-justify:inter-ideograph;line-height:11.0pt;\">\r\n	<span style=\"font-size:14px;line-height:1.5;\"><br />\r\n</span> \r\n</p>\r\n<p>\r\n	<img src=\"http://201106.image.myqcloud.com/201106/0/2cd0adce-7d3d-40b2-8d80-f7f3b74bc960/original\" width=\"900\" height=\"596\" alt=\"\" /> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-align:justify;\">\r\n	<br />\r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-align:justify;\">\r\n	<span style=\"font-size:14px;\">对于卤菜，随后时间推移和演变，出现的种类繁多，风味各异。区别于各大菜系的衍生和淘汰，卤菜却一直以它独有的形式在不断的超越与发展。而作为具有“麻、辣、鲜、香”等特点的四川卤菜无论是味道上，还是色泽上都更加令人回味无穷，垂涎三尺。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-align:justify;\">\r\n	<span style=\"font-size:14px;line-height:1.5;\"><br />\r\n</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-align:justify;\">\r\n	<span style=\"font-size:14px;line-height:1.5;\"><img src=\"http://201106.image.myqcloud.com/201106/0/a4a77b08-0805-48fa-b45f-2ce89f6d758b/original\" width=\"900\" height=\"596\" alt=\"\" /><br />\r\n</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-align:justify;\">\r\n	<br />\r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-align:justify;\">\r\n	<span style=\"font-size:14px;line-height:1.5;\">作为美食新秀掌中宝，经过【何鲜卤】的纯手工卤制，多达</span><span style=\"font-size:14px;line-height:1.5;\">10</span><span style=\"font-size:14px;line-height:1.5;\">余种秘制香料的烹调翻滚。吸收了中药卤汁后的掌中宝，在口感上，保持了鲜嫩肉质的同时又脆劲十足。尽管卤菜大部分时间是作为佐酒菜，但其工艺却不简单，卤制手法十分考究，稍不注意就会出现香料盖过肉质本身的美味。不过这对于拥有十年老卤的【何鲜卤】来说，一切都控制得刚刚好。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-align:justify;\">\r\n	<span style=\"font-size:14px;line-height:1.5;\"><br />\r\n</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-align:justify;\">\r\n	<span style=\"font-size:14px;line-height:1.5;\"><img src=\"http://201106.image.myqcloud.com/201106/0/8d2f1e4f-be53-410f-b358-a511de16fc13/original\" width=\"506\" height=\"512\" alt=\"\" /><br />\r\n</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-align:justify;\">\r\n	<br />\r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-align:justify;\">\r\n	<span style=\"font-size:14px;line-height:1.5;\">由于【何鲜卤】的定位是秘制辣卤，因此，掌中宝采用的是先卤制入味再爆炒的方式，每一粒掌中宝被红红的辣椒油均匀的包裹着，光是在色泽上，就足够让人产生食欲了。而事实也确实如此，并且由于里面还加了花生粒，吃的时候香味层次就更加丰富了。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-align:justify;\">\r\n	<span style=\"font-size:12px;line-height:1.5;\"><br />\r\n</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-align:justify;\">\r\n	<span style=\"font-size:12px;line-height:1.5;\"><img src=\"http://201106.image.myqcloud.com/201106/0/24991770-aa73-4b30-8277-f4e87559ab1c/original\" width=\"900\" height=\"596\" alt=\"\" /><br />\r\n</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-align:justify;\">\r\n	<br />\r\n</p>\r\n<p class=\"MsoNormal\" style=\"margin-left:0cm;white-space:normal;\">\r\n	<b><span style=\"color:#E53333;\"><br />\r\n</span></b> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"margin-left:0cm;white-space:normal;\">\r\n	<b><span style=\"color:#E53333;\">》》》</span></b> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"margin-left:0cm;white-space:normal;\">\r\n	<span style=\"color:#E53333;\">&nbsp;</span><b style=\"font-size:12px;line-height:1.5;\"><span style=\"color:#E53333;\">如需代理或购买，喵主可前往</span></b><b style=\"font-size:12px;line-height:1.5;\"><span style=\"color:#E53333;\">V</span></b><b style=\"font-size:12px;line-height:1.5;\"><span style=\"color:#E53333;\">猫商场【美食专区】</span></b>\r\n</p>', '', '', '1');
INSERT INTO `cms_article_data` VALUES ('c81b25d19fef4216b64800a43538fedd', '<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<span style=\"font-family:&quot;font-size:14px;\">亲爱的V猫店主：</span>\r\n</p>\r\n<p>\r\n	<span style=\"font-family:&quot;font-size:14px;\">我们将于今日（9月29日）17:00上架一款南果梨，以下是产品信息预览：</span>\r\n</p>\r\n<p>\r\n	<span style=\"font-family:&quot;font-size:14px;\">产品名称：</span><span style=\"font-family:&quot;font-size:14px;\">【拼团】</span><span style=\"font-family:&quot;font-size:14px;\">辽宁鞍山海城南果梨</span><span style=\"display:none;font-family:&quot;font-size:14px;\" id=\"__kindeditor_bookmark_end_4__\"></span>\r\n</p>\r\n<p>\r\n	<span style=\"font-family:&quot;font-size:14px;\">产品规格：</span><span style=\"font-family:&quot;font-size:14px;\">5斤装 （约30个）</span>\r\n</p>\r\n<p>\r\n	<span style=\"font-family:&quot;font-size:14px;\">拼团售价：35.9元</span>\r\n</p>\r\n<p>\r\n	<span style=\"font-family:&quot;font-size:14px;\">白金店主销售利润：18元</span>\r\n</p>\r\n<p>\r\n	<span style=\"font-family:&quot;font-size:14px;\">特约店主销售利润：12元</span>\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<span style=\"font-family:&quot;font-size:14px;\">欢迎店主代理销售！</span>\r\n</p>', '', '', '1');
INSERT INTO `cms_article_data` VALUES ('c9e1a4603c5e495899805ce59367302b', '<p class=\"MsoNormal\">\r\n	<br />\r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<span style=\"font-size:14px;color:#000000;line-height:1.5;\">记忆中，喝的第一口茶是爷爷瓷杯子里泡的老鹰茶，现在回想起来，那种感觉就像周杰伦唱的一样“爷爷泡的茶有种味道叫做家”那是一种随着长大会越来越记忆犹新的味道。就如爷爷所说的话一样，人生就像一杯茶，不会苦一辈子，但总会苦一阵子，苦过之后就是回甘了。</span><span></span> \r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<span style=\"font-size:14px;color:#000000;line-height:1.5;\"><br />\r\n</span> \r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<img src=\"http://201106.image.myqcloud.com/201106/0/ebf38038-f991-419e-b31b-7ec66a45ec20/original\" width=\"500\" height=\"332\" alt=\"\" /> \r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<br />\r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<span></span> \r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<span style=\"font-size:14px;line-height:1.5;\">每当在生活上遇到困难或者阻碍时，泡上一杯好茶静静思考一下，在品茶之间，心境自然豁然开朗，每一次都让人产生不一样的感悟。事实上，不少老一辈的作家对于喝茶都情有独钟。鲁迅先生在《准风月谈》的《喝茶》中写道“有好茶，会喝好茶，是一种‘清福’</span><span style=\"font-size:14px;line-height:1.5;\">,</span><span style=\"font-size:14px;line-height:1.5;\">不过要享这‘清福’首先必须有工夫</span><span style=\"font-size:14px;line-height:1.5;\">, </span><span style=\"font-size:14px;line-height:1.5;\">其次是练出来的特别的感觉</span><span style=\"font-size:14px;line-height:1.5;\">。”</span><span></span> \r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<span style=\"font-size:14px;line-height:1.5;\"><br />\r\n</span> \r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<img src=\"http://201106.image.myqcloud.com/201106/0/1681abff-8093-455a-89f8-6a1b3eb83092/original\" width=\"1200\" height=\"798\" alt=\"\" /> \r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<br />\r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<span style=\"font-size:14px;line-height:1.5;\">茶道起源于中国，在唐朝或更早以前，世人就将茶饮作为一种修身养性之道。因为快到年关了，所以想采购一些茶叶赠送给朋友。正好赶上V猫小店做活动，成功抢到了【0元拿样】的一罐武夷山茶里德大红袍（乌龙）。</span> \r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<span style=\"font-size:14px;line-height:1.5;\"><br />\r\n</span> \r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<span style=\"font-size:14px;line-height:1.5;\"><img src=\"http://201106.image.myqcloud.com/201106/0/5a0f04c3-1213-4a3b-a5f6-fa4c6458a7ee/original\" width=\"540\" height=\"917\" alt=\"\" /><br />\r\n</span> \r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<br />\r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<span style=\"font-size:14px;line-height:1.5;\">拿到手后，首先被【茶里德】的包装给惊艳到了。没想到仅仅售价49元的茶叶，在包装上如此精致，金色设计感罐装搭配极具中国风字体，显得韵味十足。简洁大气的设计品质丝毫不逊色于几百块茶叶的包装，十分上档次。</span> \r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<br />\r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<img src=\"http://201106.image.myqcloud.com/201106/0/ef0d9175-a820-4ef5-ad25-332e917ff6c3/original\" width=\"540\" height=\"622\" alt=\"\" /> \r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<img src=\"http://201106.image.myqcloud.com/201106/0/1e1950dd-7e4a-44d4-a36d-582fbf8baca5/original\" width=\"540\" height=\"698\" alt=\"\" /> \r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<br />\r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<span style=\"font-size:14px;\">细节处理也很用心，尽管罐子密封性比较好，但【茶里德大红袍】还是进行了第二次密封处理，这样的设计即使是在潮湿的南方应该也能保护茶叶的干燥度以及口感。</span> \r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<span style=\"font-size:14px;\"><br />\r\n</span> \r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<span style=\"font-size:14px;\"><img src=\"http://201106.image.myqcloud.com/201106/0/e8dddd0f-d778-4caf-b27c-623123ce8ff7/original\" width=\"900\" height=\"1233\" alt=\"\" /><br />\r\n</span> \r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<br />\r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<span style=\"font-size:14px;line-height:1.5;\">除了精致的包装，【茶里德大红袍】的内在也非常有料，堪称“表里如一”。给大家描述一下，这个罐子大概有20cm高，里面装的100g茶叶和罐子差不多高。</span> \r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<br />\r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<img src=\"http://201106.image.myqcloud.com/201106/0/b86aea7f-2188-4a42-8e2a-287fadeaf7f2/original\" width=\"900\" height=\"996\" alt=\"\" /> \r\n</p>\r\n<p style=\"text-align:justify;background:white;\">\r\n	<br />\r\n</p>\r\n<p style=\"text-align:justify;background:white;\">\r\n	<span style=\"font-size:14px;line-height:1.5;\">事实上，抢购这款【茶里德大红袍】的茶叶还有一个重要的原因，介绍说【茶里德】的茶叶全部采自武夷山。众所周知，武夷山向来就是产好茶的地方，非常有名气。果然，这款大红袍没有让我失望。干茶外形条形匀整，根根色泽乌黑，光是干茶就清香扑鼻了。</span><span></span> \r\n</p>\r\n<p style=\"text-align:justify;background:white;\">\r\n	<br />\r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<img src=\"http://201106.image.myqcloud.com/201106/0/550400b6-72e0-4369-a780-35e6281d3955/original\" width=\"900\" height=\"1398\" alt=\"\" /> \r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<br />\r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<span style=\"font-size:14px;line-height:1.5;\">洗茶过后，头一泡茶汤汤色饱满，清澈透亮。喝上一口，入口舌尖略涩，但细品之间却又甘甜醇香。说到大红袍的功效，除了提神醒脑，还有保护牙齿，降血降脂、抵抗衰老的作用，所以适合任何年龄阶段的人群饮用。</span><span></span> \r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<span style=\"font-size:14px;line-height:1.5;\"><br />\r\n</span> \r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<span style=\"font-size:14px;line-height:1.5;\"><img src=\"http://201106.image.myqcloud.com/201106/0/b122b928-7f21-4729-a684-a9d65a6ead55/original\" width=\"900\" height=\"754\" alt=\"\" /><br />\r\n</span> \r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<img src=\"http://201106.image.myqcloud.com/201106/0/f82b2279-03fa-4f4e-8e3a-ce073cbef863/original\" width=\"727\" height=\"421\" alt=\"\" /> \r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<br />\r\n</p>\r\n<p class=\"MsoNormal\">\r\n	大红袍这一类茶比较特别，泡<span>6</span>、<span>7</span>次都还是色泽均匀。泡上一杯热茶，在气温逐渐转冷的现在真算得上是一种享受。\r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<br />\r\n</p>\r\n<p class=\"MsoNormal\">\r\n	并且据传大红袍还有一个很美好的传说，中在明朝年间一个赶考举人路过武夷时，突然发病，腹痛难忍，当时有位来自天心岩天心寺的僧人取出采自寺旁岩石上生长的茶泡给他喝，病痛即止，不药而愈，举人后来考取了状元，为了答谢和尚，专程前来拜谢，并将身穿的状元袍披在那株茶树上，该茶因而得名“大红袍”。 <span></span> \r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<br />\r\n</p>\r\n<p class=\"MsoNormal\">\r\n	总体来说就是性价比很高，相当划算，拿来当作手信送客户送朋友完全不会有掉档次的感觉。\r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<br />\r\n</p>\r\n<p class=\"MsoNormal\" style=\"white-space:normal;\">\r\n	<span style=\"font-size:14px;line-height:1.5;\"><br />\r\n</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"white-space:normal;\">\r\n	<span style=\"font-size:14px;line-height:1.5;\">&nbsp;</span><b style=\"font-size:14px;line-height:1.5;\"><span style=\"color:#E53333;\">》》》</span></b><span style=\"font-size:14px;line-height:1.5;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"margin-left:0cm;white-space:normal;\">\r\n	<span style=\"color:#E53333;\">&nbsp;</span><b style=\"font-size:12px;line-height:1.5;\"><span style=\"color:#E53333;\">如需代理或购买，喵主可前往找货源</span></b><b style=\"font-size:12px;line-height:1.5;\"><span style=\"color:#E53333;\">【茶饮专区】</span></b> \r\n</p>\r\n<p>\r\n	<br />\r\n</p>', '', '', '1');
INSERT INTO `cms_article_data` VALUES ('cae917dc52c4471ba1e740c62103e1ab', '<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<span style=\"font-size: 14px; font-family: &quot;Microsoft YaHei&quot;;\" microsoft=\"\" yahei\";\"=\"\" font-size:14px;\"=\"\">亲爱的V猫店主：</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-family:\" font-size:14px;\"=\"\"><span style=\"font-size:14px;font-family:&quot;\" \"=\"\">我们将于下周二（</span><strong><span style=\"color:#E53333;font-size:14px;font-family:&quot;\" \"=\"\">9月20日</span></strong><span style=\"font-size:14px;font-family:&quot;\" \"=\"\">）邀请千人微商团队领袖——浅笑，开展为期1个月的微商达人培训课程，每期课程时长为40分钟~1个小时，本周培训时间为</span><strong><span style=\"color:#E53333;font-size:14px;font-family:&quot;\" \"=\"\">周二</span></strong><span style=\"font-size:14px;font-family:&quot;\" \"=\"\">、</span><strong><span style=\"color:#E53333;font-size:14px;font-family:&quot;\" \"=\"\">周四</span></strong><span style=\"font-size:14px;font-family:&quot;\" \"=\"\">、</span><strong><span style=\"color:#E53333;font-size:14px;font-family:&quot;\" \"=\"\">周六晚8点</span></strong><span style=\"font-size:14px;font-family:&quot;\" \"=\"\">，全部课程将在微信社群中进行，欢迎大家扫描下方二维码进入社群。</span></span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size: 14px; font-family: &quot;Microsoft YaHei&quot;;\" microsoft=\"\" yahei\";\"=\"\" font-size:14px;\"=\"\">第一周课程内容预览：</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size: 14px; font-family: &quot;Microsoft YaHei&quot;;\" microsoft=\"\" yahei\";\"=\"\" font-size:14px;\"=\"\">周二（9月20日）：农业微商平台的现状及未来前景</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size: 14px; font-family: &quot;Microsoft YaHei&quot;;\" microsoft=\"\" yahei\";\"=\"\" font-size:14px;\"=\"\">周四（9月22日）：平台入门操作培训（下单流程，使用说明）</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size: 14px; font-family: &quot;Microsoft YaHei&quot;;\" microsoft=\"\" yahei\";\"=\"\" font-size:14px;\"=\"\">周六（9月24日</span><span style=\"font-size: 14px; font-family: &quot;Microsoft YaHei&quot;;\" microsoft=\"\" yahei\";\"=\"\" font-size:14px;\"=\"\">）：收集问题反馈，一问一答</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-family:\" font-size:14px;\"=\"\"><span style=\"white-space:normal;font-size:14px;font-family:&quot;\" \"=\"\">（下周培训时间待定，确认后我们将以系统消息形式告知各位店主。）</span><br />\r\n</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size: 14px; font-family: &quot;Microsoft YaHei&quot;;\" microsoft=\"\" yahei\";\"=\"\" font-size:14px;\"=\"\">导师介绍</span> \r\n</p>\r\n<p>\r\n	<img src=\"http://image.v-cat.cn/201106/0/7f0dd268-3e2f-4453-a895-77ae920a6d8f/original\" width=\"640\" height=\"940\" alt=\"\" /><span style=\"font-family:\" font-size:14px;\"=\"\"></span> \r\n</p>\r\n<p>\r\n	<span style=\"font-family:\" font-size:14px;\"=\"\"><img src=\"http://image.v-cat.cn/201106/0/f54d05a6-b86d-42f3-b7e7-66d28df61fcf/original\" width=\"499\" height=\"475\" alt=\"\" /></span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size: 14px; font-family: &quot;Microsoft YaHei&quot;;\" microsoft=\"\" yahei\";\"=\"\" font-size:14px;\"=\"\">（扫码进入）</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-family: &quot;Microsoft YaHei&quot;;\" font-size:14px;\"=\"\"> <span style=\"font-size:14px;\">备注：若人数达到上限，扫码后不能进入社群，请联系V猫总管（微信号：vmao222）拉您入群。</span></span>\r\n</p>', '', '', '1');
