INSERT INTO `cms_article_data` VALUES ('cf43293b040d4a63b7d5317b453d78ca', '<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<span style=\"font-size:14px;font-family:&quot;\" \"=\"\">亲爱的V猫店主：</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:14px;font-family:;\" \"=\"\"><span style=\"font-family:Microsoft YaHei;\">9月20日，我们在攀枝花务本乡的凯特芒基地将正式开园。</span><span style=\"color:#E53333;font-family:&quot;\"><strong>今晚8：30</strong></span><span style=\"font-family:Microsoft YaHei;\">开始招募众筹庄园主，以下为活动介绍：</span></span> \r\n</p>\r\n<p>\r\n	<span style=\"white-space: normal; font-size: 14px; color: rgb(229, 51, 51); font-family: &quot;Microsoft YaHei&quot;;\" color:#e53333;\"=\"\"><strong>庄园位置：攀枝花务本乡</strong></span><span style=\"font-size:14px;font-family:\" color:#e53333;\"=\"\"><strong></strong></span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size: 14px; color: rgb(229, 51, 51); font-family: &quot;Microsoft YaHei&quot;;\" color:#e53333;\"=\"\"><strong>众筹活动价：49.8元</strong></span><span style=\"font-size: 14px; color: rgb(229, 51, 51); font-family: &quot;Microsoft YaHei&quot;;\" color:#e53333;\"=\"\"><strong>； &nbsp;</strong></span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:14px;font-family:;\" \"=\"\"><span style=\"color:#E53333;font-family:&quot;\"><strong>活动时间：9月1日 20:30~9</strong></span><span style=\"color:#E53333;font-family:&quot;\"><strong>月4日 17:00</strong></span></span><br />\r\n<span style=\"font-size: 14px; color: rgb(229, 51, 51); font-family: &quot;Microsoft YaHei&quot;;\" color:#e53333;\"=\"\"><strong>开园时间：9月2</strong></span><span style=\"font-size: 14px; color: rgb(229, 51, 51); font-family: &quot;Microsoft YaHei&quot;;\" color:#e53333;\"=\"\"><strong>0日</strong></span><br />\r\n<span style=\"font-size: 14px; color: rgb(229, 51, 51); font-family: &quot;Microsoft YaHei&quot;;\" color:#e53333;\"=\"\"><strong>挂牌回报：尊享约0.1亩果树挂牌权；</strong></span><br />\r\n<span style=\"font-size: 14px; color: rgb(229, 51, 51); font-family: &quot;Microsoft YaHei&quot;;\" color:#e53333;\"=\"\"><strong>果子回报：获得凯