INSERT INTO `cms_article_data` VALUES ('eb742c7a33e24fe1ba7f207997134b01', '<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<br />\r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-family:微软雅黑;color:#64451D;font-size:14px;\">【V猫有话说】<span style=\"color:#64451D;font-family:微软雅黑, sans-serif;font-size:14px;line-height:24px;background-color:#FFFFFF;\">目前很多微商的状态就像早期阶段的淘宝，产品同质化情况极其严重。此外，目前开设微店的大多是个人小卖家，因此除了产品同质化严重之外，在商品质量上也无法把控，对商家的考核监管难度很大，距离理想状态还有比较大的差距。“当高品质企业进入微商，就会出现微店小卖家被洗牌。”</span></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-family:微软雅黑;color:#64451D;font-size:14px;\"><br />\r\n</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"color:#000000;font-family:微软雅黑;font-size:16px;line-height:21px;\">V猫小店的出现，严格把关产品质量，以优质货源赢取广大用户的信赖。在众多的“优品”中，想要把它们“</span><span style=\"font-family:微软雅黑;font-size:16px;line-height:21px;\">据为己有</span><span style=\"font-family:微软雅黑;font-size:16px;line-height:21px;\">”</span><span style=\"font-family:微软雅黑;font-size:16px;line-height:21px;\">，上架到自己的V猫小店？</span><span style=\"font-family:微软雅黑;font-size:16px;\">V猫今天手把手教你玩V猫小店，放心，经过V猫的细心调教，哪怕你再再小白，都能秒变大咖！</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<br />\r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-family:微软雅黑;color:#333333;font-size:12pt;\"><img src=\"http://201106.image.myqcloud.com/201106/0/f034fcf7-2f7a-479d-a770-d9e6ec2fb1ba/original\" width=\"650\" height=\"1135\" alt=\"\" /><br />\r\n</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-family:微软雅黑;color:#333333;font-size:12pt;\"><br />\r\n</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-family:微软雅黑;color:#333333;font-size:12pt;\">看哪个顺眼就选哪个，任性！美食、水果、美妆、美饰、美酒……达人圈供货市场上，服饰、包包、鞋袜、化妆品、饰品、食品零食、生活用品、以及小商品等等，精致商品给您开店。海量一手优势货源，直接与厂商合作！无需进货压货，不经过任何经销商，所有我们保证了产品的低价优质。</span><span style=\"font-family:微软雅黑;color:#333333;font-size:12pt;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-family:微软雅黑;color:#333333;font-size:12pt;\"><br />\r\n</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-family:微软雅黑;color:#333333;font-size:12pt;\"><img src=\"http://201106.image.myqcloud.com/201106/0/e6f0b14b-10b5-4961-ac9c-b8fe72abde29/original\" width=\"650\" height=\"1135\" alt=\"\" /><br />\r\n</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-family:微软雅黑;color:#333333;font-size:12pt;\"><br />\r\n</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-family:微软雅黑;color:#333333;font-size:12pt;\">V猫小店，支持一键上架。只需轻轻点击一下上架，瞬间帮您搞定，轻松铺货到“我的小店”，即可开业销售。分类细致，货源充足。尽情刷货，你，一定是个圈中好手！</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-family:微软雅黑;color:#333333;font-size:12pt;\"><br />\r\n</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<br />\r\n</p>', '', '', '1');
INSERT INTO `cms_article_data` VALUES ('eced2644d3344f269f88cb9fc0aa27cd', '<p>\r\n	<span style=\"font-family:\'Microsoft YaHei\';font-size:14px;line-height:1.5;\">各位亲爱的喵主：</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-family:\'Microsoft YaHei\';font-size:14px;line-height:1.5;\">今日与黄老五供应商联系过，得到的消息反馈是现阶段因黄老五快递爆仓压力，所有快件物流揽收后挤压在中转中心分批派发（因已在中转站派发，可能派发不能分先后），请各位喵主耐心等待！（另：因爆仓压力，线上所有黄老五产品<span style=\"color:#E53333;\"><strong>1月23日17点</strong></span>之后的支付订单将于<span style=\"color:#E53333;\"><strong>2月15日</strong></span>后统一派发，请各位喵主注意下单时间噢！）</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-family:\'Microsoft YaHei\';font-size:14px;line-height:1.5;\">感谢各位喵主对V猫小店的支持和理解！&nbsp;</span> \r\n</p>', '', '', '1');
