INSERT INTO `cms_article_data` VALUES ('f0b0d64e12594ddfb971f4957326c1b9', '<p>\r\n	<span style=\"font-family:\'Microsoft YaHei\';font-size:14px;line-height:1.5;\"><br />\r\n</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-family:\'Microsoft YaHei\';font-size:14px;line-height:1.5;\">今天，你发现“消息”了吗？</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-family:\'Microsoft YaHei\';font-size:14px;\">V猫小店有一个提醒你赚钱的功能你发现了吗？</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-family:\'Microsoft YaHei\';font-size:14px;\">只需点击首页下方第二个“消息”按钮，就可以看到几个非常有用的功能哦！</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-family:\'Microsoft YaHei\';font-size:16px;\"><img src=\"http://201106.image.myqcloud.com/201106/0/10f967cd-8608-473d-8599-dc838515488a/original\" width=\"2910\" height=\"2580\" alt=\"\" /><br />\r\n</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-family:\'Microsoft YaHei\';font-size:16px;\"><span style=\"color:#E53333;font-family:\'Microsoft YaHei\';font-size:14px;\"><strong>V猫新动态</strong></span><span style=\"font-size:14px;font-family:\'Microsoft YaHei\';\">：通过这个版块，喵们可以第一时间了解V猫小店最新优惠政策、体验官招募信息等。</span></span>\r\n</p>\r\n<p>\r\n	<span style=\"font-family:\'Microsoft YaHei\';font-size:16px;\"><span style=\"color:#E53333;font-size:14px;font-family:\'Microsoft YaHei\';\"><strong>订单消息</strong></span><span style=\"font-family:\'Microsoft YaHei\';font-size:14px;\">：随时随地追踪订单物流情况。</span></span>\r\n</p>\r\n<p>\r\n	<span style=\"font-family:\'Microsoft YaHei\';font-size:16px;\"><span style=\"font-family:\'Microsoft YaHei\';font-size:14px;\"><span style=\"color:#E53333;\"><strong>财富消息</strong></span>：返佣消息、社群分红、提现消息及时查看。</span></span>\r\n</p>\r\n<p>\r\n	<span style=\"font-family:\'Microsoft YaHei\';\"><span style=\"font-size:14px;line-height:21px;\"><span style=\"color:#E53333;\"><strong>分享奖励消息</strong></span>：每一期的分享得现金信息都能再这里查阅。</span></span>\r\n</p>', '', '', '1');
INSERT INTO `cms_article_data` VALUES ('f0e818b07e4f43bea14c9bd790eb281f', '<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<span style=\"font-size:14px;font-family:&quot;\">亲爱的V猫店主：</span>\r\n</p>\r\n<p>\r\n	<span style=\"font-size:14px;font-family:&quot;\">由于突尼斯软籽石榴销售火爆，库存即将售罄，该产品将于今日（9月29日）18:00下架，望店主知悉。</span>\r\n</p>\r\n<p>\r\n	<span style=\"font-size:14px;font-family:&quot;\">感谢您的支持，谢谢！</span>\r\n</p>', '', '', '1');
INSERT INTO `cms_article_data` VALUES ('f185ff0de4384ab1bc2433c6857f6679', '<p class=\"MsoNormal\">\r\n	<span style=\"font-family:微软雅黑;font-size:10.5pt;\">越早玩V猫小店就越好：</span><span style=\"font-family:微软雅黑;font-size:10.5pt;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<span style=\"font-family:微软雅黑;font-size:10.5pt;\">为什么这么说呢？原因是酱紫的：</span><span style=\"font-family:微软雅黑;font-size:10.5pt;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<span style=\"font-family:微软雅黑;font-size:10.5pt;\"><span style=\"font-size:16px;color:#E53333;\"><strong>首先</strong></span>，当你通过下载注册成为v猫小店店主后，分享邀请码给好友加入你的战队后，即可各自获得30元v猫币，分享人数累积增加v猫币，无上限！无上限！！无上限！！！（重要的事情说三遍）同时，战队好友成交的每一笔交易你都将获得销售利润的10%-30%的分红。</span><span style=\"font-family:微软雅黑;font-size:10.5pt;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<span style=\"font-family:微软雅黑;font-size:10.5pt;\"><span style=\"font-size:16px;color:#E53333;\"><strong>其次</strong></span>，你所组建的战队是一支不可复制的超级战队，也就是说，经过你邀请到v猫小店的战队好友将只能忠实于你一人，不能再被朋友圈的其他人重复邀请。</span><span style=\"font-family:微软雅黑;font-size:10.5pt;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<span style=\"font-family:微软雅黑;font-size:10.5pt;\">最后，我必须严肃的告诉你，由于福利太好，很快就会传遍街头巷尾，所以千万别等到人都走光了才想起组建战队！</span> \r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<br />\r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<span style=\"font-family:微软雅黑;font-size:10.5pt;\"><img src=\"http://201106.image.myqcloud.com/201106/0/1351b92d-183c-4d5a-b878-31eeee683667/original\" width=\"600\" height=\"300\" alt=\"\" /><br />\r\n</span> \r\n</p>', '', '', '1');
INSERT INTO `cms_article_data` VALUES ('f1d088b4804b4ac3baa52a748fa3a0be', '<p>\r\n	<span style=\"font-size:14px;font-family:\'Microsoft YaHei\';\">亲爱的店主：</span>\r\n</p>\r\n<p>\r\n	<span style=\"font-size:14px;font-family:\'Microsoft YaHei\';\">双十一促销活动即将在一小时后结束，明天开始，很多商品的价格要调回去！望大家抓紧时间下单，标准化单品可以适当的囤一点货，比如：原本果子、黄老五、波斯贡枣、坚果全系列等等。三只馋猫是买一送一活动最后一小时，喜欢的抓住最后机会。</span>\r\n</p>\r\n<p>\r\n	<span style=\"font-size:14px;font-family:\'Microsoft YaHei\';\">感谢您的支持，谢谢！</span>\r\n</p>', '', '', '1');
INSERT INTO `cms_article_data` VALUES ('f1e64dfcafec42e8b68d4a0729edc833', '<p class=\"MsoNormal\" align=\"center\" style=\"margin-left:0cm;text-align:center;\">\r\n	<b><span style=\"font-size:10.5pt;font-family:\'Microsoft YaHei\';color:white;background:#E53333;\"><br />\r\n</span></b> \r\n</p>\r\n<p class=\"MsoNormal\" align=\"center\" style=\"margin-left:0cm;text-align:center;\">\r\n	<b><span style=\"font-size:10.5pt;font-family:\'Microsoft YaHei\';color:white;background:#E53333;\">【茶里德品牌】</span></b><span style=\"font-size:9pt;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<span style=\"font-family:\'Microsoft YaHei\';font-size:14px;\">四川茶里德贸易有限公司，始终坚持“让茶叶讲自己的故事”品牌价值观。所有的产品和服务都有其价值，</span><span style=\"font-family:微软雅黑, sans-serif;\"><span style=\"font-size:14px;font-family:\'Microsoft YaHei\';\">并秉承</span><span style=\"font-size:14px;font-family:\'Microsoft YaHei\';\">“</span><span style=\"font-size:14px;font-family:\'Microsoft YaHei\';\">品质为上，诚信第一</span><span style=\"font-size:14px;font-family:\'Microsoft YaHei\';\">”</span><span style=\"font-size:14px;font-family:\'Microsoft YaHei\';\">的经营宗旨，旨在用茶叶本身的价值来诠释品牌力量。</span><span></span></span> \r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<span style=\"font-family:微软雅黑, sans-serif;\"><span style=\"font-size:14px;font-family:\'Microsoft YaHei\';\"><img src=\"http://201106.image.myqcloud.com/201106/0/218e297a-921b-45f9-8ba4-11f5ad71eac5/original\" width=\"750\" height=\"750\" alt=\"\" /></span></span> \r\n</p>\r\n<p class=\"MsoNormal\" align=\"center\" style=\"margin-left:0cm;text-align:center;\">\r\n	<b><span style=\"font-size:10.5pt;font-family:\'Microsoft YaHei\';color:#E53333;\">为什么代理【茶里德】？？？</span></b><span style=\"font-size:9pt;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\" align=\"center\" style=\"margin-left:0cm;text-align:center;\">\r\n	<b><span style=\"font-size:10.5pt;font-family:\'Microsoft YaHei\';color:white;background:#E53333;\">【丰厚的代理收入】</span></b><span style=\"font-size:9pt;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"margin-left:0cm;\">\r\n	<span style=\"font-size:9pt;font-family:\'Microsoft YaHei\';\">&nbsp;</span> \r\n</p>\r\n<p class=\"MsoListParagraph\" style=\"margin-left:18.0pt;text-indent:-18.0pt;\">\r\n	<span style=\"font-size:10.5pt;\"><span style=\"font-family:\'Microsoft YaHei\';color:#E53333;\">1、</span><span style=\"font-size:7pt;line-height:normal;font-family:\'Times New Roman\';\"> </span></span><span style=\"font-size:10.5pt;color:#E53333;\"><span style=\"font-family:Microsoft YaHei;\">单品代理利润</span><span style=\"font-family:\'Microsoft YaHei\';\">33%</span></span><span style=\"font-size:10.5pt;\"><span style=\"font-family:Microsoft YaHei;\">（例：代理茶里德私房茶礼盒装售价为</span><span style=\"font-family:\'Microsoft YaHei\';\">168</span><span style=\"font-family:Microsoft YaHei;\">元，利润高达</span><span style=\"font-family:\'Microsoft YaHei\';\">57</span><span style=\"font-family:Microsoft YaHei;\">元）。</span></span><span style=\"font-size:9pt;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"margin-left:18.0pt;text-indent:-18.0pt;\">\r\n	<span style=\"font-size:10.5pt;font-family:\'Microsoft YaHei\';color:#E53333;\">2</span><span style=\"font-size:10.5pt;font-family:\'Microsoft YaHei\';color:#E53333;\">、</span><span style=\"font-size:10.5pt;font-family:\'Microsoft YaHei\';color:#E53333;\">战队分红高达代理利润的</span><span style=\"font-size:10.5pt;color:#E53333;font-family:\'Microsoft YaHei\';\">10%—20%</span><span style=\"font-size:10.5pt;font-family:\'Microsoft YaHei\';\">（例：战队好友销售出价值</span><span style=\"font-size:10.5pt;font-family:\'Microsoft YaHei\';\">168</span><span style=\"font-size:10.5pt;font-family:\'Microsoft YaHei\';\">元的产品，好友赚得代理利润的同时你还将获得额外战队分红）。</span><span style=\"font-size:9pt;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"margin-left:0cm;\">\r\n	<span style=\"font-size:10.5pt;font-family:\'Microsoft YaHei\';color:#E53333;\">3</span><span style=\"font-size:10.5pt;font-family:\'Microsoft YaHei\';color:#E53333;\">、</span><span style=\"font-size:10.5pt;font-family:\'Microsoft YaHei\';\">产品不定期参加</span><span style=\"font-size:10.5pt;font-family:\'Microsoft YaHei\';color:#E53333;\">分享奖励活动</span><span style=\"font-size:10.5pt;font-family:\'Microsoft YaHei\';\">，将产品信息分享到社交圈，获得现金奖励（活动详情请关注推送消息）。</span><span style=\"font-size:9pt;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"margin-left:0cm;\">\r\n	<span style=\"font-size:9pt;font-family:\'Microsoft YaHei\';\">&nbsp;</span> \r\n</p>\r\n<p class=\"MsoNormal\" align=\"center\" style=\"margin-left:0cm;text-align:center;\">\r\n	<b><span style=\"font-size:14px;font-family:\'Microsoft YaHei\';color:white;background:#E53333;\">【强大的产品优势】</span></b><span style=\"font-size:9pt;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\" align=\"center\" style=\"text-align:center;\">\r\n	<b><span style=\"font-size:10.5pt;color:#E53333;\"></span></b> \r\n</p>\r\n<p class=\"MsoNormal\" align=\"center\" style=\"text-align:center;\">\r\n	<b><span style=\"font-size:10.5pt;font-family:\'Microsoft YaHei\';color:#E53333;\">————</span></b><b><span style=\"font-size:10.5pt;color:#E53333;\"> </span></b><b><span style=\"font-size:14px;font-family:\'Microsoft YaHei\';color:#E53333;\">品质保证</span></b><b><span style=\"font-size:10.5pt;color:#E53333;\"> </span></b><b><span style=\"font-size:14px;font-family:\'Microsoft YaHei\';color:#E53333;\">用心选好茶</span></b><b><span style=\"font-size:10.5pt;color:#E53333;\"> </span></b><b><span style=\"font-size:10.5pt;font-family:\'Microsoft YaHei\';color:#E53333;\">————</span></b><b><span style=\"font-size:10.5pt;\"></span></b> \r\n</p>\r\n<span style=\"font-size:10.5pt;font-family:\'Microsoft YaHei\';\">将茶叶品质视为经营之根本的茶里德，每年都将根据时节，去往不同茶叶种类优质产区内百家茶农手中精心挑选，以专业精致的烘培技艺，独自建厂对制茶环节进行全时管家式管理，力求将质最优的茶叶呈现出其最真实的味道。</span><span style=\"font-size:11pt;font-family:\'Microsoft YaHei\';\">&nbsp;</span> \r\n<p>\r\n	<br />\r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-align:center;\">\r\n	<span style=\"font-family:\'Microsoft YaHei\';font-size:14.6667px;line-height:22px;\"><img src=\"http://201106.image.myqcloud.com/201106/0/c2a66150-e2d4-4d3c-8da8-c6a3d85896c1/original\" width=\"330\" height=\"220\" alt=\"\" /></span><br />\r\n<span style=\"font-size:14.6667px;line-height:22px;\"><span style=\"font-family:\'Microsoft YaHei\';\"></span></span> \r\n</p>\r\n<p class=\"MsoNormal\" align=\"center\" style=\"text-align:center;\">\r\n	<b><span style=\"color:#E53333;font-size:14px;\"><b style=\"text-align:center;white-space:normal;\"><span style=\"font-size:10.5pt;font-family:\'Microsoft YaHei\';color:#E53333;\">————专注做茶</span></b></span></b><b style=\"font-family:\'Microsoft YaHei\';\"><span style=\"color:red;\">&nbsp;</span></b><b style=\"font-family:\'Microsoft YaHei\';\"><span style=\"font-family:\'color:red;color:#E53333;\"><span style=\"color:#E53333;font-size:14px;\"><span style=\"font-size:14px;color:#E53333;\">推崇</span>茶文化</span><span style=\"color:#E53333;font-size:14px;\"><b style=\"text-align:center;white-space:normal;\"><span style=\"font-size:10.5pt;font-family:\'Microsoft YaHei\';color:#E53333;\">————</span></b></span></span></b> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"font-family:\'Microsoft YaHei\';\">\r\n	<span style=\"font-size:10.5pt;font-family:微软雅黑, sans-serif;\"><span style=\"font-size:14px;color:#000000;\">茶叶源于中国</span><span style=\"color:#000000;font-size:14px;\">，茶叶最早是被作为祭品使用的。随着时代变迁，西晋以后民间就将茶作为普通饮料，而如今人们更是将喝茶作为了修身养性的不二佳品。因此，茶德里结合茶用价值和现代人的生活饮食习惯，精心挑选了好茶盛产之地福建武夷山的茶叶。</span></span><span style=\"font-size:10.5pt;font-family:Arial, sans-serif;color:#333333;\"></span> \r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<a href=\"http://m.v-cat.cn/buyer/views/goods.html?type=1&shopId=${shopId}&productId=1ed731d609e247ac9913c34c8ba90284\" target=\"_blank\"><img src=\"http://201106.image.myqcloud.com/201106/0/195e26f1-f05d-4bf3-ab88-1c8e49b17c0b/original\" width=\"640\" height=\"640\" alt=\"\" style=\"line-height:1.5;\" /></a> \r\n</p>\r\n<p class=\"MsoNormal\" align=\"center\" style=\"text-align:center;\">\r\n	<span style=\"font-family:Microsoft YaHei;\">(正山小种)</span><o:p></o:p>\r\n</p>\r\n<p class=\"MsoNormal\" align=\"center\" style=\"text-align:center;\">\r\n	<span style=\"font-family:Microsoft YaHei;\"><br />\r\n</span> \r\n</p>\r\n<a href=\"http://m.v-cat.cn/buyer/views/goods.html?type=1&shopId=${shopId}&productId=b4952c74efcf4a658c7c4c68d34fc4ed 正文:\" target=\"_blank\"><img src=\"http://201106.image.myqcloud.com/201106/0/f26f760e-d847-4563-a95a-1473af7aad44/original\" width=\"750\" height=\"708\" alt=\"\" /></a> \r\n<p class=\"MsoNormal\" align=\"center\" style=\"text-align:center;\">\r\n	<span style=\"font-family:Microsoft YaHei;\">（大红袍）</span><o:p></o:p>\r\n</p>\r\n<p class=\"MsoNormal\" align=\"center\" style=\"text-align:center;\">\r\n	<span style=\"font-family:Microsoft YaHei;\"><br />\r\n</span> \r\n</p>\r\n<p class=\"MsoNormal\" align=\"center\" style=\"text-align:center;\">\r\n	<span style=\"font-family:Microsoft YaHei;\"><a href=\"http://m.v-cat.cn/buyer/views/goods.html?type=1&shopId=${shopId}&productId=3f7687d6e2154a43a430506785348ecf\" target=\"_blank\"><img src=\"http://201106.image.myqcloud.com/201106/0/5229da05-adcb-4c53-8523-1a48b0988356/original\" width=\"720\" height=\"720\" alt=\"\" /></a></span><span style=\"font-family:\'Microsoft YaHei\';line-height:1.5;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\" align=\"center\" style=\"text-align:center;\">\r\n	<span style=\"font-family:Microsoft YaHei;\"> </span> \r\n</p>\r\n<p class=\"MsoNormal\" align=\"center\" style=\"text-align:center;\">\r\n	<span style=\"font-size:12px;font-family:微软雅黑, sans-serif;color:#333333;\">（金骏眉）</span><span style=\"font-size:10.5pt;font-family:Arial, sans-serif;color:#333333;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\" align=\"center\" style=\"text-align:center;\">\r\n	<span style=\"font-size:12px;font-family:微软雅黑, sans-serif;color:#333333;\"><br />\r\n</span> \r\n</p>\r\n<p class=\"MsoNormal\" align=\"center\" style=\"margin-left:0cm;text-align:center;\">\r\n	<b><span style=\"font-size:14